INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-chicago-2020-2029-wrigley-field', 'Чикаго 2020-2029 — Wrigley Field и Cubs', 'Квест переносит игрока на стадион 1914 года, где плющ на стенах, гимн болельщиков и память о победе 2016 года создают уникальную атмосферу.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "buy_ticket_enter", "text": "Приобрести билет и попасть на стадион до начала матча", "type": "interact", "target": "wrigley_field", "count": 1, "optional": false}, {"id": "see_ivy_exhibition", "text": "Увидеть знаменитый плющ и музейную экспозицию об истории команды", "type": "explore", "target": "wrigley_ivy_museum", "count": 2, "optional": false}, {"id": "watch_nine_innings", "text": "Пережить девять иннингов и гимн «Take Me Out to the Ball Game»", "type": "interact", "target": "cubs_game_seventh_inning", "count": 1, "optional": false}, {"id": "study_2016_victory", "text": "Изучить экспозицию о победе 2016 года и исчезновении проклятия", "type": "investigate", "target": "curse_broken_exhibition", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -60, "items": [{"item_id": "achievement_cubs_fan", "quantity": 1}], "reputation": {}, "unlocks": ["sports_quests", "baseball_culture_access"]}'::jsonb, '[{"branch_id": "cubs_superfan", "description": "Стать суперфанатом Cubs", "consequences": {"skills": {"sports": 25, "culture": 15}, "reputation": {"Cubs_Fans": 30}, "unlocks": ["exclusive_baseball_access"]}}]'::jsonb, '{"metadata": {"id": "quest-chicago-2020-2029-wrigley-field", "title": "Чикаго 2020-2029 — Wrigley Field и Cubs", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "baseball", "wrigley-field"], "topics": ["sports_culture", "urban_history"], "related_systems": ["narrative-service", "world-simulation"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/chicago/2020-2029/quest-005-cubs-wrigley-field.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линейке Чикаго требовалось событие, подчеркивающее легендарность Wrigley Field и фанатскую культуру Cubs.", "goal": "Описать матч MLB, ритуалы болельщиков и историческую справку о «проклятии козла».", "essence": "Квест переносит игрока на стадион 1914 года, где плющ на стенах, гимн болельщиков и память о победе 2016 года создают уникальную атмосферу.", "key_points": ["Стадион Wrigley Field — второе по возрасту поле MLB с живым плющом на стенах.", "История «проклятия козла» длится 71 год до прорыва в 2016 году.", "Фанаты исполняют «Take Me Out to the Ball Game» на седьмом иннинге."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "buy_ticket_enter", "text": "Приобрести билет и попасть на стадион до начала матча", "type": "interact", "target": "wrigley_field", "count": 1, "optional": false}, {"id": "see_ivy_exhibition", "text": "Увидеть знаменитый плющ и музейную экспозицию об истории команды", "type": "explore", "target": "wrigley_ivy_museum", "count": 2, "optional": false}, {"id": "watch_nine_innings", "text": "Пережить девять иннингов и гимн «Take Me Out to the Ball Game»", "type": "interact", "target": "cubs_game_seventh_inning", "count": 1, "optional": false}, {"id": "study_2016_victory", "text": "Изучить экспозицию о победе 2016 года и исчезновении проклятия", "type": "investigate", "target": "curse_broken_exhibition", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -60, "items": [{"item_id": "achievement_cubs_fan", "quantity": 1}], "reputation": {}, "unlocks": ["sports_quests", "baseball_culture_access"]}, "branches": [{"branch_id": "cubs_superfan", "description": "Стать суперфанатом Cubs", "consequences": {"skills": {"sports": 25, "culture": 15}, "reputation": {"Cubs_Fans": 30}, "unlocks": ["exclusive_baseball_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Посетить игру Chicago Cubs на Wrigley Field, ощутить фанатскую культуру и узнать, как легенды и суеверия влияют на\\nгородской дух.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Wrigley Field открыт в 1914 году и славится плющом на стенах аутфилда.\\n- «Проклятие козла» длилось с 1945 по 2016 год, пока Cubs не выиграли World Series.\\n- Chicago Cubs — один из старейших клубов MLB.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Приобрести билет и попасть на стадион до начала матча.\\n2. Увидеть знаменитый плющ и музейную экспозицию об истории команды.\\n3. Пережить девять иннингов и гимн «Take Me Out to the Ball Game».\\n4. Изучить экспозицию о победе 2016 года и исчезновении проклятия.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500.\\n- Валюта: -60.\\n- Атрибут «Спорт»: +20.\\n- Ачивка: «Фанат Кабс».\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}]}, "appendix": {"glossary": [{"term": "Проклятие козла", "definition": "Суеверие, связанное с неудачами Chicago Cubs после 1945 года."}], "references": [], "decisions": []}, "implementation": {"github_issue": 1187, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики спортивного квеста, бейсбольной культуры и исторических традиций."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован квест «Wrigley Field и Cubs» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

