INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-007-paul-revere-ride', 'Бостон — Поездка Пола Ревира', 'Игроки следуют пути Ревира, активируют сигналы, предупреждают поселения и ускоряют мобилизацию ополчения.', 'side', 10, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "prepare_signal", "text": "Обеспечить сигнал «One if by land, two if by sea» в Old North Church", "type": "interact", "target": "old_north_church", "count": 1, "optional": false}, {"id": "cross_charlestown", "text": "Переправиться через Boston → Charlestown, избегая обнаружения патрулями", "type": "travel", "target": "charlestown_ferry", "count": 1, "optional": false}, {"id": "ride_lexington", "text": "Проехать Charlestown → Lexington, управляя выносливостью лошади", "type": "travel", "target": "lexington_road", "count": 1, "optional": false}, {"id": "warn_militia", "text": "Предупредить ополчение в Lexington и Concord", "type": "dialogue", "target": "militia_npc", "count": 2, "optional": false}, {"id": "hide_supplies", "text": "Спрятать боеприпасы от британских патрулей", "type": "interact", "target": "supply_cache", "count": 1, "optional": true}]'::jsonb, '{"experience": 3000, "money": {"type": "eddies", "value": 0}, "reputation": {"history": 25}, "unlocks": {"achievements": [{"id": "freedom_rider", "name": "Всадник Свободы"}], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-007-paul-revere-ride", "title": "Бостон — Поездка Пола Ревира", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["boston", "revolution", "heritage"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "heritage-service", "events-service"], "related_documents": [{"id": "questline-boston-revolution", "relation": "collection"}, {"id": "github-issue-310", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/310", "relation": "migrated_to", "migrated_at": "2025-11-22T19:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-007-paul-revere-ride.md", "visibility": "internal", "audience": ["concept", "narrative", "events"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исторический маршрут Пола Ревира описан без ветвлений и событийного сопровождения.", "goal": "Реализовать интерактивную реконструкцию ночной поездки с алармами «One if by land, two if by sea».", "essence": "Игроки следуют пути Ревира, активируют сигналы, предупреждают поселения и ускоряют мобилизацию ополчения.", "key_points": ["Сцены в Paul Revere House и Old North Church с выбором сигналов фонарей.", "Ветвления маршрутов к Lexington и Concord с проверками скрытности и выносливости.", "Системы наследия и событий получают данные для годовщин революции."]}, "quest_definition": {"quest_type": "side", "level_min": 10, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "prepare_signal", "text": "Обеспечить сигнал «One if by land, two if by sea» в Old North Church", "type": "interact", "target": "old_north_church", "count": 1, "optional": false}, {"id": "cross_charlestown", "text": "Переправиться через Boston → Charlestown, избегая обнаружения патрулями", "type": "travel", "target": "charlestown_ferry", "count": 1, "optional": false}, {"id": "ride_lexington", "text": "Проехать Charlestown → Lexington, управляя выносливостью лошади", "type": "travel", "target": "lexington_road", "count": 1, "optional": false}, {"id": "warn_militia", "text": "Предупредить ополчение в Lexington и Concord", "type": "dialogue", "target": "militia_npc", "count": 2, "optional": false}, {"id": "hide_supplies", "text": "Спрятать боеприпасы от британских патрулей", "type": "interact", "target": "supply_cache", "count": 1, "optional": true}], "rewards": {"experience": 3000, "money": {"type": "eddies", "value": 0}, "reputation": {"history": 25}, "unlocks": {"achievements": [{"id": "freedom_rider", "name": "Всадник Свободы"}], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "prologue", "title": "Пролог и подготовка", "body": "Игрок встречает NPC-резидентов North End, собирает информацию о британском движении и выбирает напарников. Перед стартом нужно обеспечить сигнал «One if by land, two if by sea» в Old North Church.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "midnight_ride", "title": "Ночная поездка", "body": "- Этап Boston → Charlestown: паром или мост, проверка на обнаружение патрулями.\\n- Charlestown → Lexington: управление выносливостью лошади и динамические события с курьерами.\\n- Lexington → Concord: плавающие задачи предупредить ополчение и спрятать боеприпасы.\\n", "mechanics_links": ["mechanics/gameplay/travel-events.yaml"], "assets": []}, {"id": "outcomes", "title": "Исходы и последствия", "body": "Успех активирует сценарий «Minute Men Ready», ускоряя появление союзных отрядов в революционной кампании. Провалы увеличивают британский патрульный прессинг и открывают миссии по спасению связистов.\\n", "mechanics_links": ["mechanics/social/social-reputation.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и аналитика", "body": "- 3 000 XP, показатель «История» +25, титул «Всадник Свободы».\\n- heritage-service фиксирует процент предупреждённых поселений, events-service планирует годовщины с экспедициями.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1206, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-boston-2029-007"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Создан квест реконструкции поездки Пола Ревира."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

