INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-dallas-bbq-texas-style-2020-2029', 'Даллас: барбекю по-техасски', 'Игрок стоит в очереди, заказывает набор мясных блюд и получает длительный бафф здоровья.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "choose_bbq_restaurant", "text": "Выбор культового заведения (Pecan Lodge, Lockhart Smokehouse)", "type": "interact", "target": "bbq_restaurant", "count": 1, "optional": false}, {"id": "wait_in_line", "text": "Ожидание в очереди до нескольких часов с локальными NPC", "type": "interact", "target": "bbq_line", "count": 1, "optional": false}, {"id": "order_bbq_platter", "text": "Заказ brisket, ribs и sausages с рассказом о рецептуре", "type": "interact", "target": "bbq_order", "count": 3, "optional": false}, {"id": "taste_without_sauce", "text": "Дегустация без соуса, оценка дыма пекана", "type": "interact", "target": "bbq_tasting", "count": 1, "optional": false}, {"id": "final_reflection", "text": "Финальное осознание роли BBQ в идентичности Техаса", "type": "interact", "target": "bbq_reflection", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -30, "items": [{"item_id": "achievement_bbq_pitmaster", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "texas_bbq_network"]}'::jsonb, '[{"branch_id": "master_bbq_appreciator", "description": "Стать знатоком техасского BBQ", "consequences": {"skills": {"culinary": 30}, "reputation": {"BBQ_Enthusiasts": 25}, "unlocks": ["exclusive_bbq_secrets"]}}]'::jsonb, '{"metadata": {"id": "canon-quest-dallas-bbq-texas-style-2020-2029", "title": "Даллас: барбекю по-техасски", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "cuisine", "quest"], "topics": ["food", "narrative", "quests"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/dallas/2020-2029/quest-003-bbq-texas-style.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная идентичность Далласа требовала развернутого квеста о Texas BBQ и культуре ожидания.", "goal": "Показать игроку процесс копчения brisket, очередь и ритуалы вокруг техасского барбекю.", "essence": "Игрок стоит в очереди, заказывает набор мясных блюд и получает длительный бафф здоровья.", "key_points": ["Подчёркнуты 12-часовое копчение, использование древесины пекана и культ ожидания.", "Награда даёт значительный HP-бафф как отражение плотного питания.", "Связана культурная гордость региона и гастрономический туризм."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "choose_bbq_restaurant", "text": "Выбор культового заведения (Pecan Lodge, Lockhart Smokehouse)", "type": "interact", "target": "bbq_restaurant", "count": 1, "optional": false}, {"id": "wait_in_line", "text": "Ожидание в очереди до нескольких часов с локальными NPC", "type": "interact", "target": "bbq_line", "count": 1, "optional": false}, {"id": "order_bbq_platter", "text": "Заказ brisket, ribs и sausages с рассказом о рецептуре", "type": "interact", "target": "bbq_order", "count": 3, "optional": false}, {"id": "taste_without_sauce", "text": "Дегустация без соуса, оценка дыма пекана", "type": "interact", "target": "bbq_tasting", "count": 1, "optional": false}, {"id": "final_reflection", "text": "Финальное осознание роли BBQ в идентичности Техаса", "type": "interact", "target": "bbq_reflection", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -30, "items": [{"item_id": "achievement_bbq_pitmaster", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "texas_bbq_network"]}, "branches": [{"branch_id": "master_bbq_appreciator", "description": "Стать знатоком техасского BBQ", "consequences": {"skills": {"culinary": 30}, "reputation": {"BBQ_Enthusiasts": 25}, "unlocks": ["exclusive_bbq_secrets"]}}]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест ориентирован на техасскую гастрономическую традицию: игрок изучает процесс приготовления brisket, общается с\\npitmaster и узнаёт, почему BBQ сравнивают с религией.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Выбор культового заведения (Pecan Lodge, Lockhart Smokehouse).\\n2. Ожидание в очереди до нескольких часов с локальными NPC.\\n3. Заказ brisket, ribs и sausages с рассказом о рецептуре.\\n4. Дегустация без соуса, оценка дыма пекана.\\n5. Финальное осознание роли BBQ в идентичности Техаса.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Копчение brisket длится 12+ часов, мясо тает во рту.\\n- Дым древесины пекана придаёт характерный аромат.\\n- Очереди в лучших местах могут занимать до трёх часов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Финансовые расходы: -30.\\n- Бафф здоровья +35% на 12 часов.\\n- Достижение «BBQ Pitmaster».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные события и баффы от уникальных блюд.\\n- Социальные взаимодействия во время ожидания.\\n- Экономические эффекты гастрономического туризма.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест расширяет кулинарную ветку Далласа и открывает сценарии о фермах, ярмарках и фуд-фестивалях штата.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1175, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики кулинарного квеста, баффы от еды и техасской BBQ-культуры."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «Барбекю по-техасски» с этапами, наградами и кулинарным контекстом."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

