INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-rodeo', 'Даллас 2020-2029 — Родео Техаса', 'Игрок становится участником зрелищного родео, наблюдая bull riding, barrel racing и живую country-музыку.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "prepare_for_rodeo", "text": "Приобрести билет и подготовить ковбойскую экипировку", "type": "interact", "target": "rodeo_preparation", "count": 1, "optional": false}, {"id": "explore_fairgrounds", "text": "Осмотреть ярмарочные павильоны и познакомиться с быками и всадниками", "type": "explore", "target": "fairgrounds", "count": 1, "optional": false}, {"id": "watch_competitions", "text": "Наблюдать bull riding, barrel racing и работы с лассо", "type": "interact", "target": "rodeo_competitions", "count": 3, "optional": false}, {"id": "participate_dance", "text": "Принять участие в танцах под country-музыку", "type": "interact", "target": "country_dance", "count": 1, "optional": false}, {"id": "complete_experience", "text": "Зафиксировать впечатления и получить бонус к культурной репутации", "type": "interact", "target": "rodeo_reflection", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -40, "items": [{"item_id": "achievement_texas_cowboy", "quantity": 1}], "reputation": {}, "unlocks": ["rodeo_quests", "cowboy_culture_access"]}'::jsonb, '[{"branch_id": "master_cowboy", "description": "Стать мастером ковбойской культуры", "consequences": {"skills": {"culture": 30}, "reputation": {"Cowboy_Culture": 30}, "unlocks": ["exclusive_rodeo_access"]}}]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-rodeo", "title": "Даллас 2020-2029 — Родео Техаса", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "rodeo", "cowboy-culture"], "topics": ["cultural_events", "sports_entertainment"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/dallas/2020-2029/quest-005-rodeo.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В культурной ветке Далласа не хватало квеста, раскрывающего традицию техасского родео.", "goal": "Структурировать сценарий посещения State Fair of Texas с акцентом на соревнования и атмосферу ковбоев.", "essence": "Игрок становится участником зрелищного родео, наблюдая bull riding, barrel racing и живую country-музыку.", "key_points": ["Родео подчёркивает историческую ковбойскую идентичность региона.", "Соревнования включают bull riding, barrel racing и демонстрации лассо.", "Атмосфера поддерживается музыкой, костюмами и традиционной атрибутикой."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "prepare_for_rodeo", "text": "Приобрести билет и подготовить ковбойскую экипировку", "type": "interact", "target": "rodeo_preparation", "count": 1, "optional": false}, {"id": "explore_fairgrounds", "text": "Осмотреть ярмарочные павильоны и познакомиться с быками и всадниками", "type": "explore", "target": "fairgrounds", "count": 1, "optional": false}, {"id": "watch_competitions", "text": "Наблюдать bull riding, barrel racing и работы с лассо", "type": "interact", "target": "rodeo_competitions", "count": 3, "optional": false}, {"id": "participate_dance", "text": "Принять участие в танцах под country-музыку", "type": "interact", "target": "country_dance", "count": 1, "optional": false}, {"id": "complete_experience", "text": "Зафиксировать впечатления и получить бонус к культурной репутации", "type": "interact", "target": "rodeo_reflection", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -40, "items": [{"item_id": "achievement_texas_cowboy", "quantity": 1}], "reputation": {}, "unlocks": ["rodeo_quests", "cowboy_culture_access"]}, "branches": [{"branch_id": "master_cowboy", "description": "Стать мастером ковбойской культуры", "consequences": {"skills": {"culture": 30}, "reputation": {"Cowboy_Culture": 30}, "unlocks": ["exclusive_rodeo_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока на State Fair of Texas, где проходят ключевые соревнования родео и демонстрируется ковбойская культура.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Приобрести билет и подготовить ковбойскую экипировку.\\n2. Осмотреть ярмарочные павильоны и познакомиться с быками и всадниками.\\n3. Наблюдать bull riding, barrel racing и работы с лассо.\\n4. Принять участие в танцах под country-музыку.\\n5. Зафиксировать впечатления и получить бонус к культурной репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Родео в Техасе — многолетняя традиция, где bull riding считается главным испытанием. Ковбойские шляпы,\\nсапоги и country-музыка формируют единый стиль мероприятия.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 40 единиц валюты\\n- Атрибут «Культура»: +20\\n- Ачивка: «Ковбой Техаса»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает зрелищные события, культурные активности и социальные взаимодействия с NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с ковбойской линией Далласа и событиями State Fair of Texas, может продолжаться заданиями в Fort Worth Stockyards.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1177, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики культурного квеста, родео-соревнований и ковбойской культуры."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Родео Техаса» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

