INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-fort-worth-stockyards', 'Даллас 2020-2029 — Fort Worth Stockyards', 'Игрок наблюдает за лонгхорнами, посещает родео и салуны, погружаясь в атмосферу Старого Запада.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "travel_fort_worth", "text": "Добраться из Далласа в Fort Worth и познакомиться с историей площадки", "type": "interact", "target": "fort_worth_stockyards", "count": 1, "optional": false}, {"id": "explore_museum_shops", "text": "Осмотреть музей и торговые ряды, подготовиться к прогону скота", "type": "explore", "target": "stockyards_museum", "count": 1, "optional": false}, {"id": "watch_cattle_drive", "text": "Наблюдать cattle drive в 11:30 и 16:00, взаимодействовать с ковбоями", "type": "interact", "target": "cattle_drive", "count": 2, "optional": false}, {"id": "visit_rodeo_arena", "text": "Посетить родео-арену и выполнить побочные активности (стрельба, танцы, фотозоны)", "type": "interact", "target": "rodeo_arena_activities", "count": 3, "optional": false}, {"id": "complete_saloon", "text": "Завершить визит в салуне, получив бонус репутации у ковбойских фракций", "type": "interact", "target": "stockyards_saloon", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -25, "items": [{"item_id": "achievement_old_west_cowboy", "quantity": 1}], "reputation": {}, "unlocks": ["cowboy_heritage_quests", "old_west_access"]}'::jsonb, '[{"branch_id": "true_old_west", "description": "Стать истинным ковбоем Старого Запада", "consequences": {"skills": {"culture": 30}, "reputation": {"Cowboy_Factions": 30, "Old_West_Community": 25}, "unlocks": ["exclusive_stockyards_access"]}}]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-fort-worth-stockyards", "title": "Даллас 2020-2029 — Fort Worth Stockyards", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "fort-worth", "cowboy-heritage"], "topics": ["author-events", "heritage"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/dallas/2020-2029/quest-009-fort-worth-stockyards.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о скотных дворах Форт-Уорта был доступен только в Markdown и не был формализован.", "goal": "Задокументировать поездку в Fort Worth Stockyards и ритуал дневного cattle drive.", "essence": "Игрок наблюдает за лонгхорнами, посещает родео и салуны, погружаясь в атмосферу Старого Запада.", "key_points": ["Ежедневные прогоны скота дважды в день формируют ядро события.", "Реальные ковбои и лонгхорны создают аутентичный опыт.", "Квест укрепляет понимание исторического наследия «Where the West Begins»."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "travel_fort_worth", "text": "Добраться из Далласа в Fort Worth и познакомиться с историей площадки", "type": "interact", "target": "fort_worth_stockyards", "count": 1, "optional": false}, {"id": "explore_museum_shops", "text": "Осмотреть музей и торговые ряды, подготовиться к прогону скота", "type": "explore", "target": "stockyards_museum", "count": 1, "optional": false}, {"id": "watch_cattle_drive", "text": "Наблюдать cattle drive в 11:30 и 16:00, взаимодействовать с ковбоями", "type": "interact", "target": "cattle_drive", "count": 2, "optional": false}, {"id": "visit_rodeo_arena", "text": "Посетить родео-арену и выполнить побочные активности (стрельба, танцы, фотозоны)", "type": "interact", "target": "rodeo_arena_activities", "count": 3, "optional": false}, {"id": "complete_saloon", "text": "Завершить визит в салуне, получив бонус репутации у ковбойских фракций", "type": "interact", "target": "stockyards_saloon", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -25, "items": [{"item_id": "achievement_old_west_cowboy", "quantity": 1}], "reputation": {}, "unlocks": ["cowboy_heritage_quests", "old_west_access"]}, "branches": [{"branch_id": "true_old_west", "description": "Стать истинным ковбоем Старого Запада", "consequences": {"skills": {"culture": 30}, "reputation": {"Cowboy_Factions": 30, "Old_West_Community": 25}, "unlocks": ["exclusive_stockyards_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока в Fort Worth Stockyards, чтобы пережить аутентичный день на скотных дворах с участием\\nковбоев, лонгхорнов и атмосферой Старого Запада.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться из Далласа в Fort Worth и познакомиться с историей площадки.\\n2. Осмотреть музей и торговые ряды, подготовиться к прогону скота.\\n3. Наблюдать cattle drive в 11:30 и 16:00, взаимодействовать с ковбоями.\\n4. Посетить родео-арену и выполнить побочные активности (стрельба, танцы, фотозоны).\\n5. Завершить визит в салуне, получив бонус репутации у ковбойских фракций.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Fort Worth Stockyards существуют с 1866 года и сохранили ковбойскую инфраструктуру. Длиннорогие быки (лонгхорны)\\nи костюмированные ковбои поддерживают аутентичность места. Девиз «Where the West Begins» подчёркивает переход от\\nгородского к фронтирному укладу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 25 единиц валюты\\n- Культурный атрибут: +20\\n- Ачивка: «Ковбой Старого Запада»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Сценарий комбинирует историческую реконструкцию, социальные взаимодействия и событийные активности, влияющие на\\nфракционную репутацию ковбоев.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Дополняет ковбойскую линию Далласа и может продолжаться сюжетами о ranch-экономике и торговых караванах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1181, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики исторического квеста, ковбойского наследия и Старого Запада."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест Fort Worth Stockyards в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

