INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-everything-bigger', 'Даллас 2020-2029 — Всё больше в Техасе', 'Игрок исследует мегаполис, отмечая преувеличенные масштабы и прибавляя очки за понимание техасской идентичности.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "research_trucks_highways", "text": "Исследовать парковки и автомагистрали, отмечая пикапы heavy duty и высокие внедорожники", "type": "explore", "target": "texas_vehicles", "count": 1, "optional": false}, {"id": "visit_family_restaurant", "text": "Посетить ресторан семейной кухни и оценить порции, рассчитанные на несколько человек", "type": "interact", "target": "family_restaurant", "count": 1, "optional": false}, {"id": "compare_landmarks", "text": "Сравнить масштабы знаковых объектов: AT&T Stadium, Big Tex, жилые районы с большими участками", "type": "explore", "target": "texas_landmarks", "count": 3, "optional": false}, {"id": "collect_slogans", "text": "Собрать коллекцию местных девизов и атрибутики («Don''t mess with Texas», флаг штата)", "type": "interact", "target": "texas_slogans", "count": 2, "optional": false}, {"id": "final_conclusion", "text": "Сформулировать вывод о том, как размер стал маркером региональной гордости", "type": "interact", "target": "texas_size_conclusion", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "money": 0, "items": [{"item_id": "achievement_texan_at_heart", "quantity": 1}], "reputation": {}, "unlocks": ["texas_culture_quests", "regional_identity_access"]}'::jsonb, '[{"branch_id": "embrace_texas_size", "description": "Принять философию «Everything is bigger in Texas»", "consequences": {"skills": {"culture": 25}, "reputation": {"Texas_Culture": 30}, "unlocks": ["exclusive_texas_experiences"]}}]'::jsonb, '