INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-rocky-mountains', 'Денвер 2020-2029 — Скалистые горы', 'Группа исследует национальный парк Rocky Mountain, преодолевает высотный стресс и получает редкие виды наград.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-rocky-mountains", "title": "Денвер 2020-2029 — Скалистые горы", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "rocky-mountains", "trekking"], "topics": ["outdoor-adventures", "natural-landmarks"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "exploration-service", "survival-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-001-rocky-mountains.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природная линия Денвера нуждалась в квесте о ключевом активе региона — Скалистых горах и подъёме на 14ers.", "goal": "Описать маршрут высокогорного треккинга с адаптацией к разреженному воздуху и достижением пика выше 14 000 футов.", "essence": "Группа исследует национальный парк Rocky Mountain, преодолевает высотный стресс и получает редкие виды наград.", "key_points": ["Скалистые горы находятся в часе пути от Денвера и предлагают 300 солнечных дней в году.", "Подъём на «14er» требует подготовки к разреженному воздуху и сезонным перепадам погоды.", "Флора и фауна (горные козлы, олени-элки) усиливают атмосферу живой природы."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест сопровождает группу из двух-четырёх персонажей в национальный парк Rocky Mountain. Цель — совершить треккинг\\nпо высокогорному маршруту, достигнуть отметки более 4 267 метров и зафиксировать панорамные виды.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться из Денвера до входа в парк и пройти инструктаж по безопасности.\\n2. Выполнить акклиматизацию на высоте 3 000 метров, проверив состояние команды.\\n3. Совершить восхождение на выбранный «14er», управляя ресурсами и выносливостью.\\n4. Наблюдать дикую фауну, зафиксировать фото и данные для природного архива.\\n5. Спуститься в лагерь и получить награду за покорение вершины.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Денвер, расположенный на высоте 1 609 метров, служит идеальной базой для походов. Сезонный климат и 300 солнечных\\nдней в году благоприятствуют треккингу, но быстрая смена погоды и разреженный воздух требуют подготовки.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 4 000 XP\\n- Расходы: 50 единиц валюты\\n- Атрибут «Природа»: +30\\n- Ачивка: «Покоритель Скалистых гор»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест делает акцент на высокогорной выносливости, управлении ресурсами и рисках гипоксии, а также открывает новые\\nмаршруты для природных исследований.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с природной линией Денвера, миссиями о национальных парках и событиями, связанными с тренировками спасателей\\nи экологическими исследованиями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1163, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Скалистые горы» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

