-- Quest: quest-denver-2029-marijuana-legalization
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-marijuana-legalization', 'Денвер 2020-2029 — Легализация марихуаны', 'Игрок посещает диспенсери, анализирует налоговые показатели и принимает решение об участии в «зелёном туризме».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-marijuana-legalization", "title": "Денвер 2020-2029 — Легализация марихуаны", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "cannabis", "economy"], "topics": ["social_change", "urban-policy"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "governance-system"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-002-marijuana-legalization.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальная линия Денвера требовала структурированного квеста о первой в США легализации рекреационной марихуаны.", "goal": "Исследовать влияние закона 2014 года на экономику, туризм и повседневную жизнь жителей.", "essence": "Игрок посещает диспенсери, анализирует налоговые показатели и принимает решение об участии в «зелёном туризме».", "key_points": ["Колорадо стало первым штатом, легализовавшим recreational cannabis в 2014 году.", "Диспенсери доступны гражданам старше 21 года и формируют миллиардные налоговые поступления.", "Легализация изменила туристические потоки и породила «green rush»."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает игроку изучить инфраструктуру легальной марихуаны в Денвере: от диспенсери и продуктивных ферм до\\nюридических норм и экономической статистики.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Изучить основы законодательства 2014 года и возрастные ограничения.\\n2. Посетить лицензированный диспенсери: ознакомиться с ассортиментом, формами потребления и системой проверки клиентов.\\n3. Проанализировать отчёты о налоговых поступлениях (около 1 млрд долларов ежегодно).\\n4. Исследовать феномен «green tourism» и оценить его влияние на город.\\n5. Принять решение: поддержать инициативы гражданских организаций или остаться наблюдателем.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Денвер стал символом легализации: сотни диспенсери обеспечивают доступ взрослым гражданам, а инициативы штата\\nнаправляют налоговые доходы в образование и инфраструктуру. Местные компании внедряют устойчивые методы выращивания.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP\\n- Расходы: 30 единиц валюты (при покупке продукции)\\n- Социальный атрибут: +20\\n- Ачивка: «Свидетель легализации»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает социальные исследования, экономический анализ и моральный выбор, влияющий на репутацию среди разных\\nфракций города.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с социально-экономической линией Денвера, миссиями о городском планировании, а также квестами о стартапах и\\nгражданских инициативах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1164, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Легализация марихуаны» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

