-- Quest: quest-van-2029-004-asian-influence
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-van-2029-004-asian-influence', 'Ванкувер — Азиатское влияние', 'Квест проводит игрока через Richmond, Chinatown и гастрономические точки, показывая, как «Hongcouver» стал самым азиатским городом Северной Америки.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-van-2029-004-asian-influence", "title": "Ванкувер — Азиатское влияние", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T03:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "multicultural", "asian-influence"], "topics": ["timeline_author", "cultural_identity"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "questline-vancouver-culture", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/vancouver/2020-2029/quest-004-asian-influence.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется зафиксировать мультикультурную специфику Ванкувера и описать влияние азиатской диаспоры на городскую идентичность.", "goal": "Структурировать ключевые локации, кулинарные маршруты и социальные аспекты взаимодействия с азиатским населением.", "essence": "Квест проводит игрока через Richmond, Chinatown и гастрономические точки, показывая, как «Hongcouver» стал самым азиатским городом Северной Америки.", "key_points": ["45% жителей города имеют азиатское происхождение, в пригороде Richmond доля достигает 70%.", "Кулинарные маршруты охватывают димсам, суши и вьетнамскую кухню мирового уровня.", "Квест усиливает сюжетные линии о мультикультурализме и экономическом влиянии азиатских общин."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с прозвищем «Hongcouver» и показывает, как азиатская диаспора формирует образ Ванкувера.\\nИгрок посещает ключевые районы, изучает двуязычные вывески и знакомится с культурными практиками, вплетёнными в\\nповседневную жизнь города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Статистика подтверждает, что 45% населения Ванкувера имеют азиатское происхождение, а в Richmond этот показатель\\nдостигает 70%. Chinatown остаётся одним из крупнейших в Северной Америке. Локальная пресса и жители используют\\nпрозвище «Hongcouver» как отражение городской реальности.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "Этап 1 — посещение Richmond и знакомство с повседневной жизнью района. Этап 2 — изучение исторического Chinatown и\\nего архитектуры. Этап 3 — гастрономический маршрут с димсам, суши и phở. Этап 4 — анализ вывесок и локального\\nбизнеса, работающего на китайском и английском языках. Финал — фиксация мультикультурного опыта в дневнике.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение квеста даёт 1500 опыта, снижает валютный баланс на 30 единиц и добавляет +25 к показателю культуры.\\nИгрок получает ачивку «Азиатский Ванкуверец», отражающую понимание городской идентичности.\\n", "mechanics_links": [], "assets": []}, {"id": "thematic_connections", "title": "Тематические связи", "body": "Квест усиливает социальную линию Ванкувера, открывая ветки про иммиграцию, экономические связи с Гонконгом и\\nвлияние азиатских сообществ на городской бизнес. Он синхронизируется с кулинарными маршрутами и событиями\\nкультурного календаря.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 254, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста «Азиатское влияние» в структурированный YAML-формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

