-- Quest: quest-van-2029-005-real-estate-crisis
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-van-2029-005-real-estate-crisis', 'Ванкувер — Кризис недвижимости', 'Игрок исследует районы West Side, анализирует влияние китайских инвестиций и решает, как реагировать на недоступное жильё.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-van-2029-005-real-estate-crisis", "title": "Ванкувер — Кризис недвижимости", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T03:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "real-estate", "economy"], "topics": ["timeline_author", "socioeconomics"], "related_systems": ["narrative-service", "economy-service", "social-service"], "related_documents": [{"id": "economy-housing-crisis-overview", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/vancouver/2020-2029/quest-005-real-estate-crisis.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кризис стоимости жилья Ванкувера не структурирован в базе знаний и не привязан к социально-экономическим механикам.", "goal": "Систематизировать факты о ценах, иностранном капитале и последствиях для молодёжи, чтобы подключить их к квестовым веткам.", "essence": "Игрок исследует районы West Side, анализирует влияние китайских инвестиций и решает, как реагировать на недоступное жильё.", "key_points": ["Средняя стоимость дома достигает 1.5–2 миллионов долларов, квартиры — 700 тысяч и выше.", "Иностранные инвестиции приводят к пустующим особнякам и усиливают социальное напряжение.", {"Квест завершает развилка": "миграция, активизм или принятие статуса-кво."}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с экономической реальностью Ванкувера, где красота города сочетается с недоступностью жилья.\\nЧерез исследования районов и анализ статистики игрок понимает масштабы кризиса и его влияние на разные социальные\\nгруппы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Средняя цена дома составляет 1.5–2 миллиона долларов, квартиры — 700 тысяч и выше. Иностранный капитал, особенно\\nиз Китая, стимулирует рост цен и формирует явление пустующих особняков. Молодые специалисты вынуждены жить с\\nродителями или уезжать.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "Этап 1 — сбор данных о ценах на жильё и динамике рынка. Этап 2 — выезд в West Side и анализ пустующих домов.\\nЭтап 3 — интервью с местными жителями и молодёжью. Этап 4 — принятие решения: инициировать активистскую кампанию,\\nсменить город или принять статус-кво.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение квеста приносит 4000 опыта, не изменяет валютный баланс и добавляет +30 к показателю экономики. Игрок\\nполучает ачивку «Знаток кризиса» и открывает ветку социального активизма.\\n", "mechanics_links": [], "assets": []}, {"id": "thematic_connections", "title": "Тематические связи", "body": "Квест поддерживает экономическую линию Ванкувера, связывает нарратив с системами рынка недвижимости, миграции и\\nсоциальной справедливости. Он может служить триггером для мировых событий о регулировании цен.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 254, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста «Кризис недвижимости» в структурированный YAML-формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

