-- Quest: quest-denver-2029-craft-beer
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-craft-beer', 'Денвер 2020-2029 — Крафтовое пивоварение', 'Игрок исследует крупные и независимые пивоварни, посещает Great American Beer Festival и ощущает эффект высоты.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-craft-beer", "title": "Денвер 2020-2029 — Крафтовое пивоварение", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "craft-beer", "festival"], "topics": ["culinary_culture", "social_exploration"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "liveops-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-004-craft-beer-scene.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не было структурированного описания квеста о крафтовой пивной сцене Денвера.", "goal": "Зафиксировать маршрут по пивоварням и фестивалю, подчёркивая влияние высоты и разнообразие сортов.", "essence": "Игрок исследует крупные и независимые пивоварни, посещает Great American Beer Festival и ощущает эффект высоты.", "key_points": ["В Денвере работает более 150 пивоварен и проводится крупнейший в США пивной фестиваль.", "Условия высокогорья влияют на восприятие алкоголя и вкус напитков.", "Квест даёт социальные бонусы и знания о культуре крафтового пива."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока в тур по пивоварням Денвера, от крупных брендов до независимых крафтовых площадок,\\nс участием в Great American Beer Festival.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Начать с экскурсии на Coors в Golden, чтобы увидеть массовое производство.\\n2. Посетить крафтовые пивоварни Wynkoop и Great Divide для дегустации IPA, stout и lager.\\n3. Посетить Great American Beer Festival (сентябрь) и попробовать лимитированные сорта.\\n4. Отследить влияние высоты (1 600+ м): ускоренное опьянение и необходимость гидратации.\\n5. Закрепить впечатления, собрав сувенирные бокалы и записав рекомендации пивоваров.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Денвер носит прозвище «Napa Valley of Beer» благодаря более чем 150 пивоварням. Высота над уровнем моря снижает\\nсодержание кислорода и усиливает воздействие алкоголя, а фестиваль Great American Beer Festival собирает тысячи сортов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 50 единиц валюты\\n- Атрибут «Харизма»: +20% на 6 часов\\n- Ачивка: «Пивовар Денвера»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует кулинарные, социальные и физиологические механики (влияние высоты на состояние персонажа).\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с линией гастрономических квестов Денвера и может вести к заданиям о барной культуре или фестивалях.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1166, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест о крафтовом пивоварении Денвера в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

