-- Quest: quest-van-2029-006-skiing-whistler
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-van-2029-006-skiing-whistler', 'Ванкувер — Лыжи в Уистлере', 'Игрок отправляется в Whistler, чтобы пережить маршрут олимпийского уровня, раскрывая спортивную идентичность Ванкувера.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-van-2029-006-skiing-whistler", "title": "Ванкувер — Лыжи в Уистлере", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T03:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "whistler", "winter-sports"], "topics": ["timeline_author", "tourism_experience"], "related_systems": ["narrative-service", "tourism-service", "sports-service"], "related_documents": [{"id": "questline-vancouver-sports", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/vancouver/2020-2029/quest-006-skiing-whistler.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо задокументировать квест о Whistler Blackcomb и привязать его к спортивным и туристическим механикам.", "goal": "Описать путь по Sea-to-Sky Highway, ключевые активности на курорте и связь с олимпиадой 2010 года.", "essence": "Игрок отправляется в Whistler, чтобы пережить маршрут олимпийского уровня, раскрывая спортивную идентичность Ванкувера.", "key_points": ["Sea-to-Sky Highway создаёт дорогу-впечатление перед прибытием на курорт.", "Whistler Blackcomb предлагает 200+ трасс, Peak 2 Peak gondola и наследие Олимпиады 2010.", "Квест усиливает спортивную линейку и туристическую экономику региона."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест фиксирует однодневную поездку из Ванкувера в Whistler. Игрок проходит живописную дорогу, проводит день на\\nкрупнейшем курорте Северной Америки и знакомится с олимпийским наследием региона.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Whistler Blackcomb — крупнейший горнолыжный курорт континента, всего в двух часах езды. Олимпийские трассы 2010 года\\nостаются рабочими, а Peak 2 Peak gondola удерживает мировые рекорды по длине пролёта. Курорт знаменит развитой\\nинфраструктурой après-ski.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "Этап 1 — поездка по Sea-to-Sky Highway с фотозонами. Этап 2 — активность в Whistler Village и подготовка снаряжения.\\nЭтап 3 — катание по трассам различной сложности, включая олимпийские участки и Peak 2 Peak gondola. Этап 4 —\\nвечернее après-ski и возвращение в город.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест даёт 3000 опыта, уменьшает валютный баланс на 250 единиц и добавляет +30 к показателю спорта. Завершение\\nоткрывает ачивку «Олимпийский лыжник».\\n", "mechanics_links": [], "assets": []}, {"id": "thematic_connections", "title": "Тематические связи", "body": "Квест связывает спортивную линию Ванкувера с туристическими событиями, поддерживает сезонные активности и может\\nзапускать соревнования или ивенты в Whistler Village.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 254, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста «Лыжи в Уистлере» в структурированный YAML-формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

