INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-ski-resorts', 'Денвер 2020-2029 — Горнолыжные курорты Колорадо', 'Игрок отправляется из Денвера в Скалистые горы, катается на трассах всех уровней и участвует в après-ski.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-ski-resorts", "title": "Денвер 2020-2029 — Горнолыжные курорты Колорадо", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "ski", "rockies"], "topics": ["sports_culture", "outdoor_activity"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "tourism-service", "survival-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-005-skiing-resorts.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спортивная линия Денвера нуждалась в структурированном описании горнолыжного туризма.", "goal": "Описать путешествие на курорты Vail, Aspen или Breckenridge с акцентом на «champagne powder».", "essence": "Игрок отправляется из Денвера в Скалистые горы, катается на трассах всех уровней и участвует в après-ski.", "key_points": ["В Колорадо более 28 курортов с уникальным «champagne powder».", "Высокогорные условия (3000+ м) влияют на физику персонажа и требуют адаптации.", "Après-ski поддерживает социальную активность и связи с NPC."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока из Денвера в Скалистые горы для катания на мировых курортах и изучения инфраструктуры «Ski State».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Выбрать курорт (Vail, Aspen, Breckenridge) и добраться за ~2 часа из Денвера.\\n2. Пройти инструктаж по высоте и оборудованию, получить ski-pass.\\n3. Протестировать трассы различных уровней, включая powder-зоны.\\n4. Посетить après-ski вечеринки, укрепляя социальные связи.\\n5. Оценить влияние высоты и подготовиться к следующему дню катания.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Колорадо предлагает 28 курортов, 300 солнечных дней в году и powder-снег «шампанское». Высота более 3000 м делает\\nкатание уникальным и требует уважения к погодным условиям.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP\\n- Расходы: 200 единиц валюты\\n- Атрибут «Спорт»: +30\\n- Ачивка: «Лыжник Скалистых Гор»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует спортивные, высокогорные и социальные механики, добавляет временные баффы и возможные дебаффы высоты.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан со спортивной линией Денвера и квестами о высоте, может вести к заданиям в Aspen или зимним фестивалям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1167, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Горнолыжные курорты Колорадо» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

