INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-broncos-football', 'Денвер 2020-2029 — Denver Broncos', 'Игрок поддерживает команду, ощущает «Mile High Magic» и изучает наследие Orange Crush.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-broncos-football", "title": "Денвер 2020-2029 — Denver Broncos", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "nfl", "broncos"], "topics": ["sports_culture", "city_identity"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "liveops-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-006-broncos-football.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовался квест, описывающий уникальную атмосферу NFL в Денвере и эффект высоты на соперников.", "goal": "Сформировать сценарий посещения Empower Field at Mile High с акцентом на историю Broncos.", "essence": "Игрок поддерживает команду, ощущает «Mile High Magic» и изучает наследие Orange Crush.", "key_points": ["Denver Broncos — трёхкратные чемпионы Super Bowl.", "Высота 1 609 м влияет на выносливость игроков и даёт преимущество хозяевам.", "Фанаты создают «оранжевое море», усиливающее социальный эффект."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока на домашний матч Denver Broncos, демонстрируя традиции «Mile High City» и легенду Orange Crush.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть на Empower Field at Mile High и познакомиться с фанатскими зонами.\\n2. Изучить экспозицию истории команды и Super Bowl трофеи.\\n3. Наблюдать матч, учитывая влияние высоты на соперников.\\n4. Вовлечься в кричалки и ритуалы фанатов.\\n5. Проанализировать статистику и легенды Orange Crush после игры.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Денвер расположен на высоте 1 609 м (одна миля). «Mile High Magic» — феномен, когда соперники быстрее устают.\\nКоманда имеет богатую историю, включая защиту Orange Crush 1970-х и три титула Super Bowl.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 100 единиц валюты\\n- Атрибут «Спорт»: +20\\n- Ачивка: «Бронкос Фанат»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает спортивные события, социальные взаимодействия и высокогорные эффекты (дебаффы для гостей, баффы хозяев).\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Интегрируется в спортивную линию Денвера и может вести к квестам по университетским командам или высотным тренировкам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1168, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Denver Broncos» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

