INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-altitude-sickness', 'Денвер 2020-2029 — Высотная адаптация', 'Игрок проходит через симптомы (головная боль, одышка), обучается корректному режиму и получает бонус к выносливости.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-altitude-sickness", "title": "Денвер 2020-2029 — Высотная адаптация", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "altitude", "physiology"], "topics": ["survival", "environment"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "survival-service", "wellbeing-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-007-altitude-sickness.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно описать опыт туристов, сталкивающихся с высотной болезнью в «Mile High City».", "goal": "Сформировать квест адаптации к высоте 1 609 м с акцентом на физиологию и советы местных.", "essence": "Игрок проходит через симптомы (головная боль, одышка), обучается корректному режиму и получает бонус к выносливости.", "key_points": ["Денвер находится на высоте ровно одной мили и содержит на 17% меньше кислорода.", "Алкоголь и физические нагрузки действуют сильнее, требуется гидратация.", "Адаптация длится 2–3 дня и даёт устойчивый бонус."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест моделирует акклиматизацию персонажа после прибытия в Денвер: симптомы высотной болезни и методы их преодоления.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть в Денвер и зафиксировать начальные показатели здоровья.\\n2. Столкнуться с симптомами (головная боль, одышка, головокружение).\\n3. Получить советы местных NPC: пить воду, избегать алкоголя и тяжелых нагрузок.\\n4. Переждать 2–3 дня адаптации, постепенно увеличивая активность.\\n5. Получить бонус к выносливости и знание об условиях высокогорья.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Денвер называют «Mile High City»: высота 1 609 м снижает плотность воздуха на 17% и усиливает действие алкоголя.\\nЖители рекомендуют пить больше воды и двигаться медленнее первые дни.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 0 единиц валюты\\n- Атрибут «Выносливость»: +20 (после адаптации)\\n- Дебафф: -20% Stamina в течение первых 3 дней\\n- Ачивка: «Покоритель Мили»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест вводит физиологические эффекты высоты, управление дебаффами, необходимость гидратации и планирование активности.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан со спортивными и туристическими квестами Денвера, влияя на эффективность активности в первые дни.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1169, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Высотная адаптация» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

