INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-green-chile', 'Денвер 2020-2029 — Зеленый чили', 'Игрок посещает локальные рестораны, дегустирует green chile и узнаёт его роль в культуре штата.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-green-chile", "title": "Денвер 2020-2029 — Зеленый чили", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "cuisine", "green-chile"], "topics": ["culinary_culture", "local_flavor"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-008-green-chili.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В гастрономической линии Денвера не хватало квеста о региональном блюде — green chile.", "goal": "Сформировать кулинарное приключение с выбором «red or green?» и рассказом об ингредиентах Hatch chile.", "essence": "Игрок посещает локальные рестораны, дегустирует green chile и узнаёт его роль в культуре штата.", "key_points": ["Green chile используют в буррито, huevos rancheros и супах.", "Hatch chile приезжает из соседнего Нью-Мексико и считается эталонным сортом.", "В каждом заведении звучит вопрос «Red or green?», формируя особый ритуал."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест проводит игрока по ресторанам Денвера, где green chile является обязательным компонентом меню.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Найти рекомендуемое заведение и получить вопрос «Red or green?» — выбрать зелёный вариант.\\n2. Заказать burrito, huevos rancheros или burger с green chile.\\n3. Оценить остроту и аромат Hatch chile, поговорить с поваром о рецептуре.\\n4. Собрать рецепты и советы по сочетанию блюд.\\n5. Поделиться впечатлениями и открыть новые кулинарные локации.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "В Колорадо green chile воспринимается как культовое блюдо: его подают почти в каждом ресторане, а лучший сорт\\nпривозят из Hatch, Нью-Мексико. Вопрос «Red or green?» стал местным паролем.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 600 XP\\n- Расходы: 15 единиц валюты\\n- Бафф здоровья: +20% HP на 6 часов\\n- Ачивка: «Green Chile Мастер»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест активирует кулинарные и социальные механики, добавляет временный бафф и расширяет карту гастрономических точек.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Дополнил гастрономическую ветку Денвера, связывая её с Tex-Mex и крафтовыми квестами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1170, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Green Chile» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

