-- Quest: quest-denver-2029-outdoor-lifestyle
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-outdoor-lifestyle', 'Денвер 2020-2029 — Активный образ жизни', 'Игрок присоединяется к жителям Денвера, выполняя череду спортивных занятий на свежем воздухе и получая бонусы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-outdoor-lifestyle", "title": "Денвер 2020-2029 — Активный образ жизни", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "outdoor", "fitness"], "topics": ["cultural_identity", "lifestyle"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-009-outdoor-lifestyle.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В фитнес-линии Денвера не хватало квеста, демонстрирующего культ активного образа жизни горожан.", "goal": "Показать ежедневный цикл outdoor-активностей и связь с природой, климатом и городской инфраструктурой.", "essence": "Игрок присоединяется к жителям Денвера, выполняя череду спортивных занятий на свежем воздухе и получая бонусы.", "key_points": ["Денвер отмечают как один из самых здоровых городов США благодаря 300 солнечным дням и доступу к горам.", "Горный велосипед, хайкинг, скалолазание и йога на открытом воздухе — часть повседневности.", "Квест укрепляет репутацию среди спортивных фракций и открывает новые активности."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит с outdoor-культурой Денвера. Игроку нужно прожить один день активного отдыха, чередуя виды спорта и\\nнаблюдая за городом, который постоянно в движении.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Утренний трейл-ран по городским паркам, сбор статистики активности жителей.\\n2. Дневной горный велосипед или хайкинг у подножия Скалистых гор.\\n3. Скалолазание или боулдеринг на локальных площадках.\\n4. Вечерняя йога на крыше или в парке, закрепление социального баффа.\\n5. Подготовка отчёта о влиянии климата и инфраструктуры на образ жизни населения.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Денвер получает около 300 солнечных дней в году и находится в часе езды от горных троп. Городские районы и пригороды\\nоборудованы велосипедными дорожками и climbing-центрами, а климат способствует круглогодичным тренировкам.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 40 единиц валюты\\n- Атрибут «Фитнес»: +25\\n- Ачивка: «Денверский атлет»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует спортивные и социальные механики, увеличивает выносливость персонажа и открывает доступ к новым\\nмаршрутам активностей.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с фитнес-линией Денвера, сюжетами о Скалистых горах и квестами, связанными с экологией и здоровьем.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1171, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Активный образ жизни» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

