-- Quest: quest-denver-2029-capitol
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-denver-2029-capitol', 'Денвер 2020-2029 — Капитолий Колорадо', 'Игрок посещает Capitol, фиксирует ключевые артефакты и укрепляет знание о государственной идентичности.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-denver-2029-capitol", "title": "Денвер 2020-2029 — Капитолий Колорадо", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["denver", "capitol", "heritage"], "topics": ["historical_landmarks", "civic_identity"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "governance-system", "education-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/denver/2020-2029/quest-010-capital-building.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В исторической линии Денвера отсутствовало структурированное описание визита в Капитолий штата.", "goal": "Показать символику здания, «ступень милю» и связь с золотой лихорадкой Колорадо.", "essence": "Игрок посещает Capitol, фиксирует ключевые артефакты и укрепляет знание о государственной идентичности.", "key_points": ["Тринадцатая ступенька соответствует высоте ровно 1 милю над уровнем моря.", "Золотой купол облицован настоящим золотом в память о лихорадке XIX века.", "Панорама Скалистых гор и музейные выставки раскрывают историю штата."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает на экскурсию в Colorado State Capitol. Игрок исследует архитектурные детали, символику и историю\\nздания, закрепляя статус гражданского покровителя региона.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Capitol и пройти регистрацию на экскурсию.\\n2. Найти 13-ю ступеньку, отметиться на «One Mile Above Sea Level».\\n3. Подняться к куполу и изучить отделку из золота.\\n4. Осмотреть выставку о золотой лихорадке и роли штата в развитии США.\\n5. Сделать панорамные снимки города и подготовить отчёт о символике.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Капитолий построен в 1894 году, купол покрыт золотом колорадских приисков. Здание расположено в «Mile High City»,\\nи посещение ступени с отметкой 1 миля — традиция туристов. Отсюда открывается вид на Скалистые горы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 800 XP\\n- Атрибут «История»: +15\\n- Ачивка: «Mile High турист»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует туристические и исторические механики, даёт доступ к новым сюжетам о государственной политике и\\nнаследии Колорадо.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с исторической линией Денвера, а также с квестами о золотой лихорадке, культурных институтах и обзорах города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1172, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Капитолий Колорадо» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

