INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-detroit-2029-motor-city', 'Детройт 2020-2029 — Город моторов', 'Игрок изучает наследие Henry Ford, посещает действующие заводы и понимает, как автомобильная революция изменила мир.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_henry_ford_museum", "text": "Посетить Henry Ford Museum в Дирборне, изучить историю Model T", "type": "interact", "target": "henry_ford_museum", "count": 1, "optional": false}, {"id": "tour_ford_rouge_factory", "text": "Пройти экскурсию по Ford Rouge Factory и увидеть сборочную линию", "type": "interact", "target": "ford_rouge_factory", "count": 1, "optional": false}, {"id": "study_big_three", "text": "Изучить экспозиции о «Большой тройке» и влиянии автошоу", "type": "interact", "target": "big_three_exhibits", "count": 1, "optional": false}, {"id": "compare_eras", "text": "Сопоставить промышленный рост 1950-х с трендами электромобилей", "type": "investigate", "target": "automotive_evolution", "count": 1, "optional": false}, {"id": "document_conclusions", "text": "Зафиксировать выводы о роли Детройта в мировом автопроме", "type": "interact", "target": "conclusions_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 4000, "money": -25, "items": [{"item_id": "achievement_automotive_magnate", "quantity": 1}], "reputation": {}, "unlocks": ["automotive_quests", "engineering_skills"]}'::jsonb, '[{"branch_id": "innovation_focus", "description": "Фокус на инновациях и технологиях", "consequences": {"skills": {"engineering": 30}, "reputation": {"Automotive_Industry": 20}, "unlocks": ["innovation_quests"]}}]'::jsonb, '{"metadata": {"id": "quest-detroit-2029-motor-city", "title": "Детройт 2020-2029 — Город моторов", "document_type": "canon", "category": "