INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-detroit-2029-motown', 'Детройт 2020-2029 — Музыка Motown', 'Игрок посещает музей Motown, погружается в атмосферу студии и получает бонусы за культурную вовлечённость.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_hitsville", "text": "Посетить дом-музей «Hitsville U.S.A.» на West Grand Boulevard", "type": "interact", "target": "motown_museum", "count": 1, "optional": false}, {"id": "study_history", "text": "Изучить хронику создания лейбла и продюсерские техники Berry Gordy", "type": "interact", "target": "museum_exhibits", "count": 1, "optional": false}, {"id": "enter_studio_a", "text": "Войти в Studio A, прослушать архивные записи и попробовать записать демо", "type": "interact", "target": "studio_a", "count": 1, "optional": false}, {"id": "learn_artists_stories", "text": "Узнать истории Supremes, Marvin Gaye, Jackson 5 и их влияние на культуру", "type": "interact", "target": "artist_exhibits", "count": 3, "optional": false}, {"id": "create_playlist", "text": "Завершить квест курацией музыкального плейлиста", "type": "interact", "target": "playlist_creation", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": -15, "items": [{"item_id": "achievement_soul_master", "quantity": 1}], "reputation": {}, "unlocks": ["music_quests", "cultural_events"]}'::jsonb, '[{"branch_id": "demo_recording", "description": "Записать демо-трек в Studio A", "consequences": {"skills": {"music": 30}, "reputation": {"Music_Industry": 15}, "unlocks": ["recording_studio_access"]}}]'::jsonb, '{"metadata": {"id": "quest-detroit-2029-motown", "title": "Детройт 2020-2029 — Музыка Motown", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_upda