"level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "analyze_debt", "text": "Собрать данные о долге $18 млрд, анализировать отчёты 2013 года", "type": "investigate", "target": "bankruptcy_reports", "count": 1, "optional": false}, {"id": "visit_abandoned_areas", "text": "Посетить заброшенные районы и задокументировать urban decay", "type": "explore", "target": "abandoned_neighborhoods", "count": 3, "optional": false}, {"id": "study_revival_initiatives", "text": "Изучить инициативы возрождения начала 2020-х и связаться с активистами", "type": "interact", "target": "local_activists", "count": 1, "optional": false}, {"id": "make_decision", "text": "Принять решение: инвестировать в проекты или сосредоточиться на исследовании", "type": "choice", "target": "revival_decision", "count": 1, "optional": false}, {"id": "prepare_report", "text": "Подготовить стратегический отчёт для городского совета", "type": "interact", "target": "city_council_report", "count": 1, "optional": false}], "rewards": {"experience": 5000, "money": 0, "items": [{"item_id": "achievement_witness_of_decline", "quantity": 1}], "reputation": {}, "unlocks": ["economic_quests", "city_planning_quests"]}, "branches": [{"branch_id": "invest_in_projects", "description": "Инвестировать в проекты возрождения", "consequences": {"money": -50000, "reputation": {"Local_Activists": 30, "City_Council": 20}, "unlocks": ["revival_projects_access"]}}, {"branch_id": "research_only", "description": "Сосредоточиться на исследовании", "consequences": {"skills": {"economics": 30}, "reputation": {"Academics": 20}, "unlocks": ["research_quests"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает в кризис Детройта. Игрок исследует архивы, общается с жителями и оценивает, как город пережил\\nкрупнейшее муниципальное банкротство в истории США.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать данные о долге $18 млрд, анализировать отчёты 2013 года.\\n2. Посетить заброшенные районы и задокументировать urban decay.\\n3. Изучить инициативы возрождения начала 2020-х и связаться с местными активистами.\\n4. Принять решение: инвестировать в проекты или сосредоточиться на исследовании.\\n5. Подготовить стратегический отчёт для городского совета.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "С 1950-х население сократилось с 1.8 млн до 700 тыс. к 2013 году. Падение автопрома и экономические кризисы привели\\nк дефициту бюджета. В 2020-х появились программы по привлечению стартапов и развитию центра.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP\\n- Атрибут «Экономика»: +30\\n- Ачивка: «Свидетель упадка»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует экономические, социальные и выборные механики, влияя на будущие сценарии развития города.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с экономической линией Детройта, квестами о промышленном наследии и миссиями по восстановлению городской среды.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1155, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики экономического квеста, интеграция с системами экономики и городского планирования."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Банкротство 2013» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

