INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-008-supreme-court', 'Вашингтон — Верховный суд США', 'Игрок входит в греческий храм права, наблюдает заседание и изучает роль девяти пожизненных судей.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_court", "text": "Пройти через вход и знакомство с историей фасада и символов", "type": "interact", "target": "supreme_court_entrance", "count": 1, "optional": false}, {"id": "visit_courtroom", "text": "Посетить courtroom, изучить правила поведения и слушать брифинг клерков", "type": "interact", "target": "courtroom_visit", "count": 1, "optional": false}, {"id": "observe_hearings", "text": "Наблюдать реальные слушания (при доступности) или AR-реконструкцию знаковых дел", "type": "interact", "target": "court_hearings_observation", "count": 1, "optional": false}, {"id": "study_judges", "text": "Посетить выставочные залы и изучить биографии девяти судей", "type": "interact", "target": "judges_exhibition", "count": 1, "optional": false}, {"id": "choose_case_position", "text": "Выбрать позицию в актуальном деле, зафиксировать её для политической линии", "type": "choice", "target": "case_position_choice", "count": 1, "optional": false}]'::jsonb, '{"xp": 2000, "currency": 0, "items": [], "reputation": {"legal": 20, "political": 10}, "achievements": [{"id": "witness_of_justice", "name": "Свидетель правосудия"}], "skill_boosts": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-008-supreme-court", "title": "Вашингтон — Верховный суд США", "document_type": "canon", "category": "lore", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "supreme-court", "judiciary"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "politics-service", "law-service"], "related_documents": [{"id": "questline-washington-judicial", "relation": "collection"}, {"id": "github-issue-379", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/379", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/washington-dc/2020-2029/quest-008-supreme-court.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "описание квеста Верховного суда не содержит сценариев слушаний, взаимодействий с судьями и юридических эффектов.", "goal": "Сформировать знание о посещении Supreme Court с учётом архитектуры, наблюдения за заседаниями и влияния на юридическую линию.", "essence": "Игрок входит в греческий храм права, наблюдает заседание и изучает роль девяти пожизненных судей.", "key_points": ["Надпись «Equal Justice Under Law» и архитектура задают тон.", "Возможность посетить courtroom и наблюдать слушания.", "Квест повышает юридическое влияние и открывает правовые сюжеты."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_court", "text": "Пройти через вход и знакомство с историей фасада и символов", "type": "interact", "target": "supreme_court_entrance", "count": 1, "optional": false}, {"id": "visit_courtroom", "text": "Посетить courtroom, изучить правила поведения и слушать брифинг клерков", "type": "interact", "target": "courtroom_visit", "count": 1, "optional": false}, {"id": "observe_hearings", "text": "Наблюдать реальные слушания (при доступности) или AR-реконструкцию знаковых дел", "type": "interact", "target": "court_hearings_observation", "count": 1, "optional": false}, {"id": "study_judges", "text": "Посетить выставочные залы и изучить биографии девяти судей", "type": "interact", "target": "judges_exhibition", "count": 1, "optional": false}, {"id": "choose_case_position", "text": "Выбрать позицию в актуальном деле, зафиксировать её для политической линии", "type": "choice", "target": "case_position_choice", "count": 1, "optional": false}], "rewards": {"xp": 2000, "currency": 0, "items": [], "reputation": {"legal": 20, "political": 10}, "achievements": [{"id": "witness_of_justice", "name": "Свидетель правосудия"}], "skill_boosts": {}}, "branches": []}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Supreme Court — высшая судебная инстанция США. Квест рассказывает о девяти пожизненных судьях, процессе\\nпринятия решений и архитектуре здания, напоминающей греческий храм.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml", "mechanics/politics/judicial-process.yaml"], "assets": []}, {"id": "regional_context", "title": "Региональные отсылки", "body": "- Надпись «Equal Justice Under Law» встречает посетителей на фасаде.\\n- Courtroom, Lady Justice и галереи формируют образовательные узлы.\\n- Решения Верховного суда формируют правовую систему США.\\n- Локация является частью юридической линии Вашингтона.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/washington-dc/index.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Пройти через вход и знакомство с историей фасада и символов.\\n2. Посетить courtroom, изучить правила поведения и слушать брифинг клерков.\\n3. Наблюдать реальные слушания (при доступности) или AR-реконструкцию знаковых дел.\\n4. Посетить выставочные залы и изучить биографии девяти судей.\\n5. Выбрать позицию в актуальном деле, зафиксировать её для политической линии.\\n", "mechanics_links": ["mechanics/progression/quest-checkpoints.yaml", "mechanics/politics/legal-decision-impact.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 000 XP, +20 юридического влияния, +10 политической репутации.\\n- Ачивка «Свидетель правосудия».\\n- Разблокировка сюжетов по судебным реформам и взаимодействия с NPC-адвокатами.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "mechanics", "title": "Особенности", "body": "- Ветвления законодательных последствий зависят от выбранной позиции игрока.\\n- Наблюдение заседаний влияет на стресс и уверенность персонажа в юридических диалогах.\\n- Сценарии соединяются с политической веткой Capitol и дипломатическими линиями.\\n", "mechanics_links": ["mechanics/emotions/stress-tracking.yaml", "mechanics/politics/faction-stance.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 379, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста «Верховный суд США» в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

