-- Quest: quest-van-2029-003-mountains-ocean
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-van-2029-003-mountains-ocean', 'Ванкувер — Горы и океан за один день', 'Игрок проживает один день «ski in the morning, beach in the afternoon», открывая преимущества географии Ванкувера и получая доступ к природной линии города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-van-2029-003-mountains-ocean", "title": "Ванкувер — Горы и океан за один день", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T03:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "geography", "outdoor"], "topics": ["timeline_author", "regional_exploration"], "related_systems": ["narrative-service", "tourism-service", "weather-service"], "related_documents": [{"id": "questline-vancouver-nature", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/vancouver/2020-2029/quest-003-mountains-ocean.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо формализовать квест, демонстрирующий уникальное сочетание гор и океана в рамках одного игрового дня.", "goal": "Зафиксировать этапы маршрута Grouse Mountain и English Bay, подчеркнув контраст природных биомов и связав их с туристическими механиками.", "essence": "Игрок проживает один день «ski in the morning, beach in the afternoon», открывая преимущества географии Ванкувера и получая доступ к природной линии города.", "key_points": ["Город позволяет за 15 минут добраться до горнолыжных трасс и вернуться на пляж до заката.", "Контраст климатических зон усиливает туристическую идентичность локации.", "Завершение квеста закрепляет ачивку и повышает показатели активности."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест рассказывает о маршруте, который подчёркивает уникальную географию Ванкувера. Утром игрок поднимается на\\nGrouse Mountain, наблюдает город с высоты, после чего возвращается к English Bay, чтобы завершить день на пляже.\\nКонтраст природных декораций становится ключевым впечатлением и катализатором сюжетной линии о балансе урбанизма\\nи дикой природы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Grouse Mountain расположен в 15 минутах от центра и предлагает ухоженные трассы с видами на залив. English Bay —\\nгородской пляж, куда жители приходят на закат. Слоган «Ski in the morning, beach in the afternoon» подчёркивает,\\nчто подобный сценарий доступен немногим мегаполисам.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "Этап 1 — забронировать поездку на Grouse Mountain и подняться на вершину. Этап 2 — провести утро на склонах,\\nотмечая панорамы города. Этап 3 — вернуться в центр и подготовиться к вечерней части. Этап 4 — встретить закат на\\nEnglish Bay, зафиксировать наблюдения в дневнике и получить бонус за контраст климатических зон.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение квеста приносит 2500 опыта, уменьшает валютный баланс на 80 единиц и добавляет +30 к показателю\\nактивности. Игрок получает ачивку «Горы и Океан», закрепляющую прохождение маршрута за один день.\\n", "mechanics_links": [], "assets": []}, {"id": "thematic_connections", "title": "Тематические связи", "body": "Квест поддерживает природную линию Ванкувера и может служить стартовой точкой для сюжетов о климатическом\\nразнообразии региона. Он связывает туристические активности с системами настроения, выносливости и фотоконтента.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 254, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста «Горы и Океан» в структурированный YAML-формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

