INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-houston-2029-nasa-space-center', 'Хьюстон 2020-2029 — Космический центр NASA', 'Игрок посещает Space Center Houston, видит Saturn V, тренажёры астронавтов и слышит легендарные переговоры.', 'main', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_space_center", "text": "Добраться до Space Center Houston и зарегистрироваться на тур", "type": "interact", "target": "space_center_houston", "count": 1, "optional": false}, {"id": "visit_mission_control", "text": "Посетить Mission Control, прослушать переговоры Apollo 13 и Artemis", "type": "interact", "target": "mission_control", "count": 1, "optional": false}, {"id": "explore_rocket_park", "text": "Осмотреть Rocket Park с ракетой Saturn V и другими образцами техники", "type": "explore", "target": "rocket_park", "count": 1, "optional": false}, {"id": "check_astronaut_trainers", "text": "Проверить тренажёры астронавтов и провести мини-тренировку", "type": "interact", "target": "astronaut_trainers", "count": 1, "optional": false}, {"id": "meet_nasa_expert", "text": "Встретиться с экспертом или ветераном NASA, получить эксклюзивные инсайты", "type": "interact", "target": "nasa_expert", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "money": -30, "items": [{"item_id": "achievement_space_explorer", "quantity": 1}], "reputation": {}, "unlocks": ["space_quests", "nasa_resources"]}'::jsonb, '[{"branch_id": "deep_training", "description": "Пройти углубленную тренировку астронавта", "consequences": {"skills": {"space": 40, "engineering": 20}, "reputation": {"NASA": 25}, "unlocks": ["advanced_space_training"]}}]'::jsonb, '{"metadata": {"id": "quest-houston-2029-nasa-space-center", "title": "Хьюстон 2020-2029 — Космический центр NASA", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["houston", "nasa", "space"], "topics": ["exploration", "technological_heritage"], "related_systems": ["narrative-service", "exploration-service", "gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/houston/2020-2029/quest-001-nasa-space-center.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Космическая линия Хьюстона требовала квеста о Johnson Space Center и его роли в миссиях Apollo и современных программах.", "goal": "Пережить экскурсию по NASA, познакомиться с Mission Control и собрать данные о космическом наследии города.", "essence": "Игрок посещает Space Center Houston, видит Saturn V, тренажёры астронавтов и слышит легендарные переговоры.", "key_points": ["Mission Control руководит пилотируемыми миссиями с 1965 года, включая Apollo 11 и Artemis.", "Экспозиции центра показывают Saturn V, тренировки астронавтов и инновационные технологии.", "Хьюстон ассоциируется с фразой «Houston, we have a problem», закрепляющей имидж космической столицы."]}, "quest_definition": {"quest_type": "main", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_space_center", "text": "Добраться до Space Center Houston и зарегистрироваться на тур", "type": "interact", "target": "space_center_houston", "count": 1, "optional": false}, {"id": "visit_mission_control", "text": "Посетить Mission Control, прослушать переговоры Apollo 13 и Artemis", "type": "interact", "target": "mission_control", "count": 1, "optional": false}, {"id": "explore_rocket_park", "text": "Осмотреть Rocket Park с ракетой Saturn V и другими образцами техники", "type": "explore", "target": "rocket_park", "count": 1, "optional": false}, {"id": "check_astronaut_trainers", "text": "Проверить тренажёры астронавтов и провести мини-тренировку", "type": "interact", "target": "astronaut_trainers", "count": 1, "optional": false}, {"id": "meet_nasa_expert", "text": "Встретиться с экспертом или ветераном NASA, получить эксклюзивные инсайты", "type": "interact", "target": "nasa_expert", "count": 1, "optional": false}], "rewards": {"experience": 5000, "money": -30, "items": [{"item_id": "achievement_space_explorer", "quantity": 1}], "reputation": {}, "unlocks": ["space_quests", "nasa_resources"]}, "branches": [{"branch_id": "deep_training", "description": "Пройти углубленную тренировку астронавта", "consequences": {"skills": {"space": 40, "engineering": 20}, "reputation": {"NASA": 25}, "unlocks": ["advanced_space_training"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест сопровождает игрока в Johnson Space Center. Необходимо пройти экскурсию, изучить исторические миссии и\\nсовременные программы NASA, чтобы расширить космическую линию кампании.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Space Center Houston и зарегистрироваться на тур.\\n2. Посетить Mission Control, прослушать переговоры Apollo 13 и Artemis.\\n3. Осмотреть Rocket Park с ракетой Saturn V и другими образцами техники.\\n4. Проверить тренажёры астронавтов и провести мини-тренировку.\\n5. Встретиться с экспертом или ветераном NASA, получить эксклюзивные инсайты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Johnson Space Center — главный центр управления пилотируемыми полётами США. Здесь базируются офисы астронавтов,\\nработают тренажёры EVA и ведутся исследования для программы Artemis. Хьюстон официально носит прозвище Space City.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP\\n- Расходы: 30 единиц валюты\\n- Атрибут «Космос»: +30\\n- Ачивка: «Космический исследователь»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает образовательные и исследовательские механики, расширяет возможности космических сюжетных арок и\\nдаёт доступ к технологическим ресурсам.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с космической линией Хьюстона, квестами о миссиях Apollo/Artemis, а также с сюжетами о научных и инженерных\\nразработках.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1143, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики космического квеста, интеграция с NASA и системами исследования космоса."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Космический центр NASA» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

