INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-van-2029-007-cannabis-culture', 'Ванкувер — Культура каннабиса', 'Игрок исследует легальные магазины, знакомится с локальными сортами «BC Bud» и решает, вовлекаться ли в механику потребления или сохранить наблюдательную позицию.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "learn_legalization", "text": "Изучить историю легализации 2018 года и либеральное отношение города", "type": "interact", "target": "legalization_history", "count": 1, "optional": false}, {"id": "visit_dispensary", "text": "Посетить магазин dispensary, ознакомиться с меню сортов и консультантами", "type": "interact", "target": "cannabis_dispensary", "count": 1, "optional": false}, {"id": "make_choice", "text": "Сделать выбор между покупкой, дегустацией или сугубо исследовательским подходом", "type": "choice", "target": "cannabis_choice", "count": 1, "optional": false}, {"id": "record_social_reaction", "text": "Зафиксировать социальную реакцию NPC и влияние на репутацию", "type": "dialogue", "target": "npc_reactions", "count": 3, "optional": false}]'::jsonb, '{"experience": 1000, "money": -25, "reputation": {"social": 15}, "items": [], "unlocks": {"achievements": ["Знаток Легализации"], "flags": ["cannabis_culture_explored"]}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-van-2029-007-cannabis-culture", "title": "Ванкувер — Культура каннабиса", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "cannabis", "social-lifestyle"], "topics": ["city_storylines", "legalization"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "quest-system", "relation": "references"}, {"id": "vancouver-city-profile", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/vancouver/2020-2029/quest-007-cannabis-culture.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание квеста фиксирует факты о легализации каннабиса, но не связывает их с нарративными и социальными механиками игры.", "goal": "Сформировать структурированное знание о маршруте и взаимодействиях игрока в каннабис-культуре Ванкувера.", "essence": "Игрок исследует легальные магазины, знакомится с локальными сортами «BC Bud» и решает, вовлекаться ли в механику потребления или сохранить наблюдательную позицию.", "key_points": ["Легализация 2018 года задаёт контекст и объясняет либеральное отношение города.", "Магазины dispensary служат точками выбора и социального взаимодействия.", "Квест подключается к культурной линии Ванкувера и формирует игровые эффекты через социальные показатели."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "learn_legalization", "text": "Изучить историю легализации 2018 года и либеральное отношение города", "type": "interact", "target": "legalization_history", "count": 1, "optional": false}, {"id": "visit_dispensary", "text": "Посетить магазин dispensary, ознакомиться с меню сортов и консультантами", "type": "interact", "target": "cannabis_dispensary", "count": 1, "optional": false}, {"id": "make_choice", "text": "Сделать выбор между покупкой, дегустацией или сугубо исследовательским подходом", "type": "choice", "target": "cannabis_choice", "count": 1, "optional": false}, {"id": "record_social_reaction", "text": "Зафиксировать социальную реакцию NPC и влияние на репутацию", "type": "dialogue", "target": "npc_reactions", "count": 3, "optional": false}], "rewards": {"experience": 1000, "money": -25, "reputation": {"social": 15}, "items": [], "unlocks": {"achievements": ["Знаток Легализации"], "flags": ["cannabis_culture_explored"]}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест демонстрирует, как Ванкувер адаптировался к легализации каннабиса. Игрок посещает легальные магазины, изучает ассортимент и узнаёт о культовых сортах «BC Bud», решая, участвовать ли в дегустации или ограничиться исследованием культурного феномена.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Канада легализовала каннабис в 2018 году, и Ванкувер одним из первых поддержал индустрию. Сети dispensary легальны, а бренд «BC Bud» ассоциируется с местными терруарами и качеством продукции.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "Этап 1 — знакомство с легализацией и историей либерального отношения города. Этап 2 — посещение магазина, ознакомление с меню сортов и консультантами. Этап 3 — выбор игрока между покупкой, дегустацией или сугубо исследовательским подходом. Этап 4 — фиксация социальной реакции NPC и влияние на репутацию.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение квеста приносит 1000 XP и прирост социального показателя на 15 пунктов. При выборе покупки расходуется 25 единиц валюты, а также разблокируется ачивка «Знаток Легализации».\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "narrative_connections", "title": "Тематические связи", "body": "Квест усиливает социальную линию Ванкувера и может запускать дополнительные события с участием местных активистов и туристов. Решение игрока отражается на восприятии города другими фракциями и доступе к культурным мероприятиям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1047, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 1047."}, {"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста «Культура каннабиса» в структурированный YAML-документ."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

