INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-street-food-chaos', 'Дели 2020-2029 — Хаос уличной еды', 'Игрок дегустирует chaats, parathas и jalebi на Chandni Chowk, балансируя между гастрономическим баффом и риском отравления.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-street-food-chaos", "title": "Дели 2020-2029 — Хаос уличной еды", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:10:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["delhi", "street_food", "spice"], "topics": ["culinary-adventures", "urban-life"], "related_systems": ["narrative-service", "buff-system", "risk-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/delhi/2020-2029/quest-003-street-food-chaos.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Тур по уличной еде Дели существовал в Markdown и не учитывал риски «Delhi Belly» и гастрономические баффы.", "goal": "Перевести сценарий в YAML с описанием ключевых блюд, рынков и вероятности негативного эффекта.", "essence": "Игрок дегустирует chaats, parathas и jalebi на Chandni Chowk, балансируя между гастрономическим баффом и риском отравления.", "key_points": ["Квест-id DELHI-2029-003, сложность medium, длительность 2–4 часа.", "Этапы включают посещение рынка, дегустацию блюд и управление здоровьем.", "Награда даёт 1 500 XP, бафф к HP и титул «Уличный гурман» с шансом негативного эффекта."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Chandni Chowk — гастрономический центр Дели, где острота специй и разнообразие блюд соперничают с риском «Delhi Belly».\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Отыскать лучшие лавки chaats, parathas, jalebi и ласси на Chandni Chowk.\\n2. Провести дегустацию, управляя уровнем специй и соблюдая гигиену.\\n3. Решить, пробовать ли самые рискованные блюда ради максимального баффа.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны традиционные блюда, ценовой диапазон (₹50-100) и местные советы по безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 10 едди, получает 1 500 XP, бафф +20% HP на 6 часов и титул «Уличный гурман». 50% шанс негативного эффекта (-30% HP).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 256, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Хаос уличной еды» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

