INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-houston-2029-oil-capital', 'Хьюстон 2020-2029 — Нефтяная столица', 'Игрок посещает штаб-квартиры корпораций, анализирует логистику порта и принимает решение по отношению к отрасли.', 'main', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_energy_corridor", "text": "Исследовать Energy Corridor, зафиксировать штаб-квартиры и инфраструктуру", "type": "explore", "target": "energy_corridor", "count": 1, "optional": false}, {"id": "visit_corporate_towers", "text": "Посетить небоскрёбы нефтяных корпораций, изучить их инвестиционные планы", "type": "interact", "target": "corporate_towers", "count": 2, "optional": false}, {"id": "tour_houston_port", "text": "Совершить тур по порту Хьюстона и оценить логистику танкеров", "type": "explore", "target": "houston_port", "count": 1, "optional": false}, {"id": "gather_opinions", "text": "Собрать мнения экологических активистов и нефтяных лоббистов", "type": "interact", "target": "activists_lobbyists", "count": 2, "optional": false}, {"id": "make_decision", "text": "Принять решение: поддержать нефтяные проекты или продвигать устойчивые альтернативы", "type": "choice", "target": "energy_decision", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "money": 0, "items": [{"item_id": "achievement_oil_magnate", "quantity": 1}], "reputation": {}, "unlocks": ["energy_quests", "corporate_network_access"]}'::jsonb, '[{"branch_id": "support_oil", "description": "Поддержать нефтяные проекты", "consequences": {"reputation": {"Oil_Companies": 30, "Energy_Industry": 25}, "money": 50000, "unlocks": ["oil_industry_access"]}}, {"branch_id": "support_green", "description": "Продвигать устойчивые альтернативы", "consequences": {"reputation": {"Environmental_Activists": 30, "Green_Energy": 25}, "unlocks": ["green_energy_quests"]}}]'::jsonb, '{"metadata": {"id": "quest-houston-2029-oil-capital", "title": "Хьюстон 2020-2029 — Нефтяная столица", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["houston", "energy", "oil"], "topics": ["economic_power", "geopolitics"], "related_systems": ["narrative-service", "economy-service", "gameplay-service", "quest-service", "character-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/houston/2020-2029/quest-002-oil-capital.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экономическая линия Хьюстона требовала квеста о статусе города как мировой столицы энергетики.", "goal": "Исследовать энергетический коридор, порт и дилеммы между нефтяной промышленностью и экологией.", "essence": "Игрок посещает штаб-квартиры корпораций, анализирует логистику порта и принимает решение по отношению к отрасли.", "key_points": ["В Хьюстоне базируется более 5 000 энергетических компаний, включая ExxonMobil и Shell.", "Порт Хьюстона — ключевой узел экспорта нефти и нефтепродуктов.", "Квест поднимает вопрос устойчивости и конкуренции между нефтяной и зелёной энергетикой."]}, "quest_definition": {"quest_type": "main", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_energy_corridor", "text": "Исследовать Energy Corridor, зафиксировать штаб-квартиры и инфраструктуру", "type": "explore", "target": "energy_corridor", "count": 1, "optional": false}, {"id": "visit_corporate_towers", "text": "Посетить небоскрёбы нефтяных корпораций, изучить их инвестиционные планы", "type": "interact", "target": "corporate_towers", "count": 2, "optional": false}, {"id": "tour_houston_port", "text": "Совершить тур по порту Хьюстона и оценить логистику танкеров", "type": "explore", "target": "houston_port", "count": 1, "optional": false}, {"id": "gather_opinions", "text": "Собрать мнения экологических активистов и нефтяных лоббистов", "type": "interact", "target": "activists_lobbyists", "count": 2, "optional": false}, {"id": "make_decision", "text": "Принять решение: поддержать нефтяные проекты или продвигать устойчивые альтернативы", "type": "choice", "target": "energy_decision", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": 0, "items": [{"item_id": "achievement_oil_magnate", "quantity": 1}], "reputation": {}, "unlocks": ["energy_quests", "corporate_network_access"]}, "branches": [{"branch_id": "support_oil", "description": "Поддержать нефтяные проекты", "consequences": {"reputation": {"Oil_Companies": 30, "Energy_Industry": 25}, "money": 50000, "unlocks": ["oil_industry_access"]}}, {"branch_id": "support_green", "description": "Продвигать устойчивые альтернативы", "consequences": {"reputation": {"Environmental_Activists": 30, "Green_Energy": 25}, "unlocks": ["green_energy_quests"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит с энергетической экосистемой Хьюстона. Игроку предстоит посетить Energy Corridor, встретиться с\\nпредставителями отрасли и оценить геополитическое влияние города.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Исследовать Energy Corridor, зафиксировать штаб-квартиры и инфраструктуру.\\n2. Посетить небоскрёбы нефтяных корпораций, изучить их инвестиционные планы.\\n3. Совершить тур по порту Хьюстона и оценить логистику танкеров.\\n4. Собрать мнения экологических активистов и нефтяных лоббистов.\\n5. Принять решение: поддержать нефтяные проекты или продвигать устойчивые альтернативы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Хьюстон получил прозвище «Energy Capital of the World» благодаря концентрации компаний upstream и downstream.\\nЭкономика города зависит от нефтегазовой отрасли, а порт — один из загруженных в США, обрабатывающий миллионы баррелей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP\\n- Атрибут «Экономика»: +25\\n- Ачивка: «Нефтяной магнат»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает экономические, корпоративные и геополитические механики, влияя на отношения с фракциями энергии\\nи экологическими движениями.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с экономической линией Хьюстона, сюжетами о порте и миссиями, посвящёнными переходу к новой энергетике.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1144, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики экономического квеста, выбор между нефтяной и зелёной энергетикой."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Нефтяная столица» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type,