INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-houston-2029-humidity-hell', 'Хьюстон 2020-2029 — Влажная жара', 'Игрок выходит на улицы Хьюстона в июле, борется с влажностью 90% и ищет способы пережить «сауну под открытым небом».', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "measure_heat", "text": "Выйти на улицу в пик жары, измерить показатели «feels like»", "type": "interact", "target": "heat_measurement", "count": 1, "optional": false}, {"id": "walk_blocks", "text": "Пройти несколько кварталов, отследить рост температуры тела и усталости", "type": "explore", "target": "city_blocks", "count": 3, "optional": false}, {"id": "find_shelter", "text": "Найти укрытие с кондиционером, восстановить ресурсы и гидратацию", "type": "interact", "target": "air_conditioned_shelter", "count": 1, "optional": false}, {"id": "compare_districts", "text": "Повторить цикл, сравнив районы с тенью, парками и бетонными пространствами", "type": "explore", "target": "different_districts", "count": 3, "optional": false}, {"id": "create_survival_guide", "text": "Составить памятку по выживанию и рекомендацию для NPC или отряда", "type": "interact", "target": "survival_guide", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": 0, "items": [{"item_id": "achievement_humidity_survivor", "quantity": 1}], "reputation": {}, "unlocks": ["survival_quests", "climate_adaptation_skills"]}'::jsonb, '[{"branch_id": "master_survival", "description": "Стать мастером выживания во влажной жаре", "consequences": {"skills": {"survival": 30, "endurance": 25}, "unlocks": ["extreme_weather_resistance"]}}]'::jsonb, '{"metadata": {"id": "quest-houston-2029-humidity-hell", "title": "Хьюстон 2020-2029 — Влажная жара", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["houston", "climate", "survival"], "topics": ["extreme_weather", "lifestyle"], "related_systems": ["narrative-service", "survival-service", "gameplay-service", "quest-service", "character-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/houston/2020-2029/quest-003-humidity-hell.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Климатическая линия Хьюстона требовала квеста, раскрывающего экстремальную влажность и её влияние на жителей.", "goal": "Смоделировать день в пиковую жару, научить игрока управлять ресурсами и предупреждать перегрев.", "essence": "Игрок выходит на улицы Хьюстона в июле, борется с влажностью 90% и ищет способы пережить «сауну под открытым небом».", "key_points": ["Летом температура достигает +38 °C при влажности 90%, что создаёт эффект +45 °C.", "Кондиционирование — жизненно важный элемент городской инфраструктуры.", "Квест демонстрирует физиологические эффекты и необходимость грамотного планирования."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "measure_heat", "text": "Выйти на улицу в пик жары, измерить показатели «feels like»", "type": "interact", "target": "heat_measurement", "count": 1, "optional": false}, {"id": "walk_blocks", "text": "Пройти несколько кварталов, отследить рост температуры тела и усталости", "type": "explore", "target": "city_blocks", "count": 3, "optional": false}, {"id": "find_shelter", "text": "Найти укрытие с кондиционером, восстановить ресурсы и гидратацию", "type": "interact", "target": "air_conditioned_shelter", "count": 1, "optional": false}, {"id": "compare_districts", "text": "Повторить цикл, сравнив районы с тенью, парками и бетонными пространствами", "type": "explore", "target": "different_districts", "count": 3, "optional": false}, {"id": "create_survival_guide", "text": "Составить памятку по выживанию и рекомендацию для NPC или отряда", "type": "interact", "target": "survival_guide", "count": 1, "optional": false}], "rewards": {"experience": 2000, "money": 0, "items": [{"item_id": "achievement_humidity_survivor", "quantity": 1}], "reputation": {}, "unlocks": ["survival_quests", "climate_adaptation_skills"]}, "branches": [{"branch_id": "master_survival", "description": "Стать мастером выживания во влажной жаре", "consequences": {"skills": {"survival": 30, "endurance": 25}, "unlocks": ["extreme_weather_resistance"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает игрока в климат Хьюстона. Необходимо пройти серию испытаний под палящим солнцем, следить за\\nсостоянием персонажа и изучить, как жители адаптируются к влажности.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Выйти на улицу в пик жары, измерить показатели «feels like».\\n2. Пройти несколько кварталов, отследить рост температуры тела и усталости.\\n3. Найти укрытие с кондиционером, восстановить ресурсы и гидратацию.\\n4. Повторить цикл, сравнив районы с тенью, парками и бетонными пространствами.\\n5. Составить памятку по выживанию и рекомендацию для NPC или отряда.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Влажность в Хьюстоне достигает 90% летом, а температура часто превышает +38 °C. Из-за близости Мексиканского залива\\nгород окружён влажным воздухом, поэтому кондиционеры присутствуют в каждом помещении, а жители планируют дела по погоде.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP\\n- Атрибут «Выносливость»: +20\\n- Дебафф: -40% Stamina при нахождении во влажной жаре\\n- Ачивка: «Выживший во влажности»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует климатические и физиологические механики, вводит систему перегрева и необходимость планирования\\nмаршрутов через зоны кондиционирования.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с климатической линией Хьюстона, квестами о штормовых угрозах и сюжетами о городской инфраструктуре.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1145, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики климатического выживания, система перегрева и планирования маршрутов."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Влажная жара» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

