INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-houston-2029-rodeo-livestock-show', 'Хьюстон 2020-2029 — Houston Livestock Show and Rodeo', 'Игрок погружается в трёхнедельное событие, наблюдая соревнования, выставки животных и выступления звёзд.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_nrg_park", "text": "Прибыть на NRG Park в первый день и изучить расписание трёхнедельного события", "type": "interact", "target": "nrg_park", "count": 1, "optional": false}, {"id": "visit_livestock_exhibits", "text": "Посетить павильоны выставки скота, познакомиться с животноводами и инновациями", "type": "interact", "target": "livestock_exhibits", "count": 1, "optional": false}, {"id": "watch_rodeo_competitions", "text": "Наблюдать за соревнованиями bull riding, barrel racing и calf roping", "type": "interact", "target": "rodeo_competitions", "count": 3, "optional": false}, {"id": "participate_fair_activities", "text": "Участвовать в дегустациях техасской кухни и ярмарочных активностях", "type": "interact", "target": "fair_activities", "count": 1, "optional": false}, {"id": "attend_concert", "text": "Завершить день на стадионе NRG, посетив концерт звезды", "type": "interact", "target": "nrg_stadium_concert", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "money": -100, "items": [{"item_id": "achievement_rodeo_king", "quantity": 1}], "reputation": {}, "unlocks": ["rodeo_quests", "texas_culture_access"]}'::jsonb, '[{"branch_id": "deep_rodeo_experience", "description": "Глубокое погружение в родео-культуру", "consequences": {"skills": {"culture": 35}, "reputation": {"Texas_Culture": 30}, "unlocks": ["exclusive_rodeo_access"]}}]'::jsonb, '{"metadata": {"id": "quest-houston-2029-rodeo-livestock-show", "title": "Хьюстон 2020-2029 — Houston Livestock Show and Rodeo", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["houston", "rodeo", "livestock"], "topics": ["cultural_events", "entertainment"], "related_systems": ["narrative-service", "social-service", "gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/houston/2020-2029/quest-004-rodeo-livestock-show.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурная линия Хьюстона не охватывала самое крупное в мире родео и выставку скота.", "goal": "Показать масштаб Houston Livestock Show and Rodeo, сочетающий родео, выставки и вечерние концерты.", "essence": "Игрок погружается в трёхнедельное событие, наблюдая соревнования, выставки животных и выступления звёзд.", "key_points": ["Родео собирает более 2,5 млн посетителей и длится три недели в марте.", "Сцена NRG Stadium принимает ведущих исполнителей country и поп-артистов.", "Выставка скота демонстрирует аграрное наследие штата и современные технологии животноводства."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_nrg_park", "text": "Прибыть на NRG Park в первый день и изучить расписание трёхнедельного события", "type": "interact", "target": "nrg_park", "count": 1, "optional": false}, {"id": "visit_livestock_exhibits", "text": "Посетить павильоны выставки скота, познакомиться с животноводами и инновациями", "type": "interact", "target": "livestock_exhibits", "count": 1, "optional": false}, {"id": "watch_rodeo_competitions", "text": "Наблюдать за соревнованиями bull riding, barrel racing и calf roping", "type": "interact", "target": "rodeo_competitions", "count": 3, "optional": false}, {"id": "participate_fair_activities", "text": "Участвовать в дегустациях техасской кухни и ярмарочных активностях", "type": "interact", "target": "fair_activities", "count": 1, "optional": false}, {"id": "attend_concert", "text": "Завершить день на стадионе NRG, посетив концерт звезды", "type": "interact", "target": "nrg_stadium_concert", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": -100, "items": [{"item_id": "achievement_rodeo_king", "quantity": 1}], "reputation": {}, "unlocks": ["rodeo_quests", "texas_culture_access"]}, "branches": [{"branch_id": "deep_rodeo_experience", "description": "Глубокое погружение в родео-культуру", "consequences": {"skills": {"culture": 35}, "reputation": {"Texas_Culture": 30}, "unlocks": ["exclusive_rodeo_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока посетить Houston Livestock Show and Rodeo. Необходимо пройти через выставку скота, ключевые\\nродео-соревнования и вечерний концерт, собирая впечатления для дальнейших сюжетов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть на NRG Park в первый день и изучить расписание трёхнедельного события.\\n2. Посетить павильоны выставки скота, познакомиться с животноводами и инновациями аграрной отрасли.\\n3. Наблюдать за соревнованиями bull riding, barrel racing и calf roping.\\n4. Участвовать в дегустациях техасской кухни и ярмарочных активностях.\\n5. Завершить день на стадионе NRG, посетив концерт звезды и получить культурный бафф.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Houston Livestock Show and Rodeo проходит с 1932 года и признано крупнейшим событием этого типа. NRG Stadium\\nпревращается в центр культурной жизни Техаса, а концерты привлекают артистов уровня George Strait и Beyoncé.\\nСобытие финансирует стипендии студентам аграрных специальностей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP\\n- Расходы: 100 единиц валюты\\n- Атрибут «Культура»: +30\\n- Ачивка: «Родео король»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест комбинирует культурные события, социальные взаимодействия и экономическую активность, влияя на отношения\\nс аграрными и развлекательными фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с культурной линией Хьюстона, квестами о ковбойском наследии и гастрономическими событиями штата.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1146, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики культурного квеста, интеграция с родео и техасской культурой."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест о Houston Livestock Show and Rodeo в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards 