INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-van-2029-010-most-livable-city', 'Ванкувер — Most Livable City', 'Игрок исследует показатели качества жизни, сталкивается с высокой стоимостью недвижимости и делает вывод, оправдывает ли город свою репутацию.', 'side', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "analyze_rating", "text": "Проанализировать рейтинг Most Livable City и оценить критерии качества жизни", "type": "interact", "target": "livability_rating_analysis", "count": 1, "optional": false}, {"id": "compare_real_problems", "text": "Сравнить рейтинг с реальными проблемами: стоимость недвижимости, климат, жилищный кризис", "type": "interact", "target": "real_problems_comparison", "count": 1, "optional": false}, {"id": "discuss_with_npc", "text": "Провести диалог с NPC и обсудить личный выбор — стоит ли качество жизни заплаченных средств", "type": "dialogue", "target": "city_resident_npc", "count": 3, "optional": false}, {"id": "make_final_judgment", "text": "Сделать итоговый вывод, фиксируемый через параметры репутации и социоэкономические флаги", "type": "choice", "target": "livability_judgment", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "money": 0, "reputation": {"philosophy": 25}, "items": [], "unlocks": {"achievements": ["Ванкуверский Житель"], "flags": ["livability_analysis_complete"]}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-van-2029-010-most-livable-city", "title": "Ванкувер — Most Livable City", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "livability", "city-analysis"], "topics": ["city_storylines", "socioeconomics"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "quest-van-2029-003-mountains-ocean", "relation": "complements"}, {"id": "quest-van-2029-005-real-estate-crisis", "relation": "contrasts"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/vancouver/2020-2029/quest-010-most-livable-city.md", "visibility": "internal", "audience": ["concept", "narrative", "analytics"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется оформить концепцию квеста про рейтинг пригодности к жизни и показать его парадоксы для баланса экономики и климата.", "goal": "Сформировать структурированный сценарий, который объясняет преимущества и издержки жизни в Ванкувере и подключает их к социально-экономическим системам игры.", "essence": "Игрок исследует показатели качества жизни, сталкивается с высокой стоимостью недвижимости и делает вывод, оправдывает ли город свою репутацию.", "key_points": ["Ванкувер стабильно входит в мировые топы по livability благодаря природе, инфраструктуре и безопасности.", "Высокая стоимость жилья и затяжные дожди создают дилемму «комфорт против доступности».", "Результат квеста влияет на репутацию города и открывает социально-экономические события."]}, "quest_definition": {"quest_type": "side", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "analyze_rating", "text": "Проанализировать рейтинг Most Livable City и оценить критерии качества жизни", "type": "interact", "target": "livability_rating_analysis", "count": 1, "optional": false}, {"id": "compare_real_problems", "text": "Сравнить рейтинг с реальными проблемами: стоимость недвижимости, климат, жилищный кризис", "type": "interact", "target": "real_problems_comparison", "count": 1, "optional": false}, {"id": "discuss_with_npc", "text": "Провести диалог с NPC и обсудить личный выбор — стоит ли качество жизни заплаченных средств", "type": "dialogue", "target": "city_resident_npc", "count": 3, "optional": false}, {"id": "make_final_judgment", "text": "Сделать итоговый вывод, фиксируемый через параметры репутации и социоэкономические флаги", "type": "choice", "target": "livability_judgment", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": 0, "reputation": {"philosophy": 25}, "items": [], "unlocks": {"achievements": ["Ванкуверский Житель"], "flags": ["livability_analysis_complete"]}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает игроку изучить рейтинг «Most Livable City», пройти через ключевые критерии (природа, инфраструктура, культура, безопасность) и оценить обратную сторону жизни — стоимость недвижимости, климат и кризис жилья.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Ванкувер стабильно занимает места в топ-3 мировых рейтингов livability. Город сочетает доступ к природе, развитые здравоохранение и образование, но сталкивается с высокими ценами на жильё и высоким уровнем осадков.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Анализ рейтинга и оценка критериев качества жизни.\\n2. Сравнение с реальными проблемами: стоимость недвижимости, климат, жилищный кризис.\\n3. Диалог с NPC и обсуждение личного выбора — стоит ли качество жизни заплаченных средств.\\n4. Итоговый вывод игрока фиксируется через параметры репутации и социоэкономические флаги.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml", "mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 3000 XP, прирост показателя «Философия» на 25 пунктов и ачивку «Ванкуверский Житель». В зависимости от выбора возможны изменения репутации и доступ к последующим сюжетам о стоимости жизни.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "narrative_connections", "title": "Тематические связи", "body": "Квест связывает природную и социально-экономическую линии Ванкувера, вливаясь в арку доступного жилья и климатических вызовов. Результат влияет на события недвижимости и реакции городских фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1040, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 1040."}, {"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста «Most Livable City» в структурированный формат с акцентом на социально-экономический парадокс."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

