INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-001-white-house', 'Вашингтон — Белый дом', 'Игрок проходит тур по Белому дому, сталкивается с усиленной охраной Secret Service и открывает политическую линию Вашингтона.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "pass_security_checkpoint", "text": "Пройти через внешние кордоны, подтвердив бронь тура и пройти досмотр Secret Service", "type": "interact", "target": "white_house_security_checkpoint", "count": 1, "optional": false}, {"id": "explore_east_wing", "text": "Попасть в East Wing и ознакомиться с парадными залами: State Dining Room, Blue Room, Green Room", "type": "interact", "target": "east_wing_rooms", "count": 1, "optional": false}, {"id": "observe_security_protocols", "text": "Наблюдать за работой персонала и протоколами безопасности в коридорах резиденции", "type": "interact", "target": "security_observation", "count": 1, "optional": false}, {"id": "view_oval_office", "text": "Завершить тур наблюдением на дистанции за Овальным кабинетом и обзором территории через ограждение", "type": "interact", "target": "oval_office_viewpoint", "count": 1, "optional": false}, {"id": "take_photos", "text": "Сделать контрольные снимки фасада и получить политический глоссарий для последующих миссий", "type": "interact", "target": "white_house_exterior_photography", "count": 1, "optional": false}]'::jsonb, '{"xp": 2000, "currency": 0, "items": [], "reputation": {"political": 20, "tourism": 10}, "achievements": [{"id": "at_the_gates_of_power", "name": "У Врат Власти"}], "skill_boosts": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-001-white-house", "title": "Вашингтон — Белый дом", "document_type": "canon", "category": "lore", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "white-house", "politics"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "tourism-service", "diplomacy-service"], "related_documents": [{"id": "questline-washington-politics", "relation": "collection"}, {"id": "github-issue-375", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/375", "relation": "migrated_to", "migrated_at": "2025-11-22T22:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/washington-dc/2020-2029/quest-001-white-house.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание «Белого дома» хранится в MD и не структурировано для интеграции в квестовый движок и политические системы.", "goal": "Перенести опыт посещения резиденции президента США в формат знания с явными этапами, наградами и связями с дипломатическими механиками.", "essence": "Игрок проходит тур по Белому дому, сталкивается с усиленной охраной Secret Service и открывает политическую линию Вашингтона.", "key_points": ["Тур требует предварительного бронирования и строгого досмотра со стороны Secret Service.", "Основные точки маршрута — East Wing, парадные помещения и наблюдение за Овальным кабинетом с дистанции.", "Квест выдает политический прогресс и ачивку «У Врат Власти»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "pass_security_checkpoint", "text": "Пройти через внешние кордоны, подтвердив бронь тура и пройти досмотр Secret Service", "type": "interact", "target": "white_house_security_checkpoint", "count": 1, "optional": false}, {"id": "explore_east_wing", "text": "Попасть в East Wing и ознакомиться с парадными залами: State Dining Room, Blue Room, Green Room", "type": "interact", "target": "east_wing_rooms", "count": 1, "optional": false}, {"id": "observe_security_protocols", "text": "Наблюдать за работой персонала и протоколами безопасности в коридорах резиденции", "type": "interact", "target": "security_observation", "count": 1, "optional": false}, {"id": "view_oval_office", "text": "Завершить тур наблюдением на дистанции за Овальным кабинетом и обзором территории через ограждение", "type": "interact", "target": "oval_office_viewpoint", "count": 1, "optional": false}, {"id": "take_photos", "text": "Сделать контрольные снимки фасада и получить политический глоссарий для последующих миссий", "type": "interact", "target": "white_house_exterior_photography", "count": 1, "optional": false}], "rewards": {"xp": 2000, "currency": 0, "items": [], "reputation": {"political": 20, "tourism": 10}, "achievements": [{"id": "at_the_gates_of_power", "name": "У Врат Власти"}], "skill_boosts": {}}, "branches": []}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Белый дом по адресу 1600 Pennsylvania Avenue служит резиденцией президента США с 1800 года. Туристическая линия\\nквеста подчёркивает протоколы безопасности, исторические интерьеры East Wing и символизм Овального кабинета,\\nкоторый остаётся недоступным для свободного посещения.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml", "mechanics/social/tourism-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Региональные отсылки", "body": "- Резиденция президента США и символ исполнительной ветви власти.\\n- Secret Service обеспечивает тотальный контроль периметра и гостевых потоков.\\n- Овальный кабинет открыт для обзора только из отдалённых точек во время тура.\\n- Белый дом является ключевой точкой политической линии Вашингтона.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/washington-dc/index.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Пройти через внешние кордоны, подтвердив бронь тура и пройти досмотр Secret Service.\\n2. Попасть в East Wing и ознакомиться с парадными залами: State Dining Room, Blue Room, Green Room.\\n3. Наблюдать за работой персонала и протоколами безопасности в коридорах резиденции.\\n4. Завершить тур наблюдением на дистанции за Овальным кабинетом и обзором территории через ограждение.\\n5. Сделать контрольные снимки фасада и получить политический глоссарий для последующих миссий.\\n", "mechanics_links": ["mechanics/security/security-clearance.yaml", "mechanics/progression/quest-checkpoints.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 000 опыта, +20 политического влияния, репутация столичного туризма +10.\\n- Ачивка «У Врат Власти».\\n- Разблокировка дипломатических квестов и доступ к событийной линии Secret Service.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики и риски", "body": "- Система безопасности: допуск через уровни допуска Secret Service, риск досмотров и задержек.\\n- Туристический маршрут: ограниченные временные слоты, жёсткое расписание, обязательная бронь.\\n- Политические системы: влияния на ветку дипломатии, информеры по работе исполнительной власти.\\n", "mechanics_links": ["mechanics/security/security-risks.yaml", "mechanics/tourism/city-tour-flow.yaml"], "assets": []}, {"id": "connections", "title": "Связи и последующие шаги", "body": "Квест открывает доступ к политической линии Вашингтона, активирует мероприятия по Capitol Building и подключает\\nигрока к дипломатическим NPC. Контент служит стартом для сюжетов Secret Service и линейки национальной безопасности.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/washington-dc/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 375, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста «Белый дом» в шаблон знания."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

