INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-002-capitol-building', 'Вашингтон — Капитолий США', 'Игрок исследует здание Конгресса, наблюдает заседание и сталкивается с памятью о штурме Капитолия.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_exterior", "text": "Пройти через внешний периметр и познакомиться с архитектурой купола", "type": "interact", "target": "capitol_exterior", "count": 1, "optional": false}, {"id": "visit_rotunda", "text": "Посетить ротонду и изучить исторические фрески и скульптуры", "type": "interact", "target": "capitol_rotunda", "count": 1, "optional": false}, {"id": "explore_statuary_hall", "text": "Осмотреть National Statuary Hall и старое помещение Верховного суда", "type": "interact", "target": "statuary_hall", "count": 1, "optional": false}, {"id": "learn_jan6_event", "text": "Прослушать интерактивный рассказ о событиях 6 января 2021 года", "type": "interact", "target": "jan6_memorial", "count": 1, "optional": false}, {"id": "observe_congress", "text": "Наблюдать за заседанием Конгресса или познакомиться с процедурой голосования через AR-сценарий", "type": "interact", "target": "congress_session", "count": 1, "optional": false}]'::jsonb, '{"xp": 2500, "currency": 0, "items": [], "reputation": {"political": 25, "democratic": 15}, "achievements": [{"id": "witness_of_democracy", "name": "Свидетель демократии"}], "skill_boosts": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-002-capitol-building", "title": "Вашингтон — Капитолий США", "document_type": "canon", "category": "lore", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "capitol", "democracy"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "politics-service", "security-service"], "related_documents": [{"id": "questline-washington-politics", "relation": "collection"}, {"id": "github-issue-375", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/375", "relation": "migrated_to", "migrated_at": "2025-11-22T22:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/washington-dc/2020-2029/quest-002-capitol-building.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест «Capitol Building» описан без структурных блоков и ссылок на политические механики, что мешает интеграции в сценарии 2020–2029.", "goal": "Сформировать знание о посещении Капитолия, учитывая исторический контекст, тур и события 6 января 2021 года.", "essence": "Игрок исследует здание Конгресса, наблюдает заседание и сталкивается с памятью о штурме Капитолия.", "key_points": ["Тур охватывает ротонду, Statuary Hall и исторические залы Верховного суда.", "Ветвление событий связывает маршрут с воспоминанием о штурме 6 января 2021 года.", "Квест даёт демократический прогресс и ачивку «Свидетель демократии»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_exterior", "text": "Пройти через внешний периметр и познакомиться с архитектурой купола", "type": "interact", "target": "capitol_exterior", "count": 1, "optional": false}, {"id": "visit_rotunda", "text": "Посетить ротонду и изучить исторические фрески и скульптуры", "type": "interact", "target": "capitol_rotunda", "count": 1, "optional": false}, {"id": "explore_statuary_hall", "text": "Осмотреть National Statuary Hall и старое помещение Верховного суда", "type": "interact", "target": "statuary_hall", "count": 1, "optional": false}, {"id": "learn_jan6_event", "text": "Прослушать интерактивный рассказ о событиях 6 января 2021 года", "type": "interact", "target": "jan6_memorial", "count": 1, "optional": false}, {"id": "observe_congress", "text": "Наблюдать за заседанием Конгресса или познакомиться с процедурой голосования через AR-сценарий", "type": "interact", "target": "congress_session", "count": 1, "optional": false}], "rewards": {"xp": 2500, "currency": 0, "items": [], "reputation": {"political": 25, "democratic": 15}, "achievements": [{"id": "witness_of_democracy", "name": "Свидетель демократии"}], "skill_boosts": {}}, "branches": []}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Капитолий — символ американской демократии, где заседают Сенат и Палата представителей. Квест фокусируется на\\nмаршрутных точках внутри здания и подчёркивает значение купола, ротонды и исторических залов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml", "mechanics/politics/legislative-process.yaml"], "assets": []}, {"id": "regional_context", "title": "Региональные отсылки", "body": "- Купол Капитолия — главный визуальный символ демократии США.\\n- Ротонда содержит фрески и статуи, рассказывающие историю страны.\\n- События 6 января 2021 года добавляют память о попытке штурма.\\n- Национальный холл статуй собирает знаковые фигуры каждого штата.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/washington-dc/index.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Пройти через внешний периметр и познакомиться с архитектурой купола.\\n2. Посетить ротонду и изучить исторические фрески и скульптуры.\\n3. Осмотреть National Statuary Hall и старое помещение Верховного суда.\\n4. Прослушать интерактивный рассказ о событиях 6 января 2021 года.\\n5. Наблюдать за заседанием Конгресса или познакомиться с процедурой голосования через AR-сценарий.\\n", "mechanics_links": ["mechanics/progression/quest-checkpoints.yaml", "mechanics/politics/session-simulation.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 500 опыта, +25 демократического влияния, репутация политической ветки +15.\\n- Ачивка «Свидетель демократии».\\n- Разблокировка событий по законотворческим сюжетам и доступа к политическим NPC.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики и конфликты", "body": "- Политическая симуляция: наблюдение за заседаниями Сената/Палаты.\\n- Безопасность: протоколы DHS и риск повышения тревоги при упоминании событий Jan 6.\\n- Образовательные сегменты: фрески, исторические лекции и AR-экскурсии.\\n", "mechanics_links": ["mechanics/security/alert-levels.yaml", "mechanics/education/museum-experiences.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест усиливает политическую сюжетную линию Вашингтона, открывает миссии Secret Service и ветку по защите\\nдемократии. Служит входом к событиям на National Mall и квестам Верховного суда.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/washington-dc/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 375, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста «Капитолий США» в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

