INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-boston-smart-city-initiative', 'Boston 2020-2029 — «Инициатива умного города»', 'Классический бостонский дух инноваций встречается с киберпанковскими технологиями в эволюции города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-boston-smart-city-initiative", "title": "Boston 2020-2029 — «Инициатива умного города»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:51:56+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["boston", "smart-city", "technology", "innovation", "infrastructure"], "topics": ["technology", "urban-planning", "innovation"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-america-boston-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-011-smart-city-initiative.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Бостон запускает амбициозную инициативу по превращению города в полностью умный мегаполис, но сталкивается с сопротивлением традиционалистов.", "goal": "Игрок помогает внедрить умные технологии в инфраструктуру Бостона, балансируя между инновациями и сохранением исторического наследия.", "essence": "Классический бостонский дух инноваций встречается с киберпанковскими технологиями в эволюции города.", "key_points": ["Умные датчики контролируют трафик и окружающую среду.", "Конфликт между технологическим прогрессом и историческим наследием.", "Финальная презентация умного города мировым лидерам."]}, "content": {"sections": [{"id": "tech_assessment", "title": "Оценка технологий", "body": "Игрок встречает городского планировщика на Boston Common. Ему демонстрируют прототипы умных технологий для контроля трафика и окружающей среды.\\n", "mechanics_links": [], "assets": []}, {"id": "historical_preservation", "title": "Сохранение истории", "body": "Историческое общество Бостона выступает против изменений. Игрок находит баланс между инновациями и сохранением Freedom Trail.\\n", "mechanics_links": [], "assets": []}, {"id": "neural_networks", "title": "Нейронные сети города", "body": "Мини-игра: настройка ИИ-систем для оптимизации городского трафика. Умные светофоры и датчики предотвращают заторы.\\n", "mechanics_links": [], "assets": []}, {"id": "smart_city_summit", "title": "Саммит умного города", "body": "Финальная презентация умного Бостона представителям других городов. Игрок защищает проект от корпоративных шпионов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 850 XP.\\n- Издержки: −32 кредов.\\n- Бафф «Умный гражданин +20% эффективности в городских задачах на 12 часов».\\n- Ачивка «Архитектор будущего».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Smart City Infrastructure", "definition": "Интегрированная система датчиков, ИИ и автоматизации для оптимизации городского функционирования."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875688, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Инициатива умного города» для Boston 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

