INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-buenos-aires-2029-006-mate-culture', 'Буэнос-Айрес — культура мате', 'Игрок покупает набор мате, обучается ритуалу совместного чаепития и укрепляет социальные связи в кварталах Буэнос-Айреса.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "buy_mate_set", "text": "Приобрести набор калабаса + bombilla + yerba mate у локального торговца", "type": "interact", "target": "mate_set_purchase", "count": 1, "optional": false}, {"id": "learn_preparation", "text": "Пройти урок подготовки напитка: заполнить калабасу, залить горячей водой, правильно разместить bombilla", "type": "interact", "target": "mate_preparation_lesson", "count": 1, "optional": false}, {"id": "participate_ritual", "text": "Участвовать в круговом ритуале, соблюдая очередность и социальные правила", "type": "interact", "target": "circular_mate_ritual", "count": 1, "optional": false}, {"id": "make_sharing_choice", "text": "Сделать выбор — поделиться напитком с NPC или сохранить личное употребление, влияя на репутацию", "type": "choice", "target": "mate_sharing_choice", "count": 1, "optional": false}]'::jsonb, '{"xp": 800, "currency": -15, "items": [], "reputation": {"social": 15}, "achievements": [{"id": "matero", "name": "Матеро"}], "skill_boosts": {"energy": 15}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-buenos-aires-2029-006-mate-culture", "title": "Буэнос-Айрес — культура мате", "document_type": "canon", "category": "narrative", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-11T05:10:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["buenos-aires", "mate", "social-ritual"], "topics": ["city_storylines", "food_culture"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "quest-system", "relation": "references"}, {"id": "buenos-aires-city-profile", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/buenos-aires/2020-2029/quest-006-mate-culture.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание традиции мате подано списком и не связывает социальный ритуал с игровыми механиками.", "goal": "Структурировать квест о культуре мате и показать, как социальное чаепитие влияет на репутацию и экономику города.", "essence": "Игрок покупает набор мате, обучается ритуалу совместного чаепития и укрепляет социальные связи в кварталах Буэнос-Айреса.", "key_points": ["Квест раскрывает происхождение калабасы и bombilla.", "Игрок получает варианты дегустации и выбор участия в ритуале.", "Социальное чаепитие усиливает репутацию и открывает локальные бонусы энергии."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "buy_mate_set", "text": "Приобрести набор калабаса + bombilla + yerba mate у локального торговца", "type": "interact", "target": "mate_set_purchase", "count": 1, "optional": false}, {"id": "learn_preparation", "text": "Пройти урок подготовки напитка: заполнить калабасу, залить горячей водой, правильно разместить bombilla", "type": "interact", "target": "mate_preparation_lesson", "count": 1, "optional": false}, {"id": "participate_ritual", "text": "Участвовать в круговом ритуале, соблюдая очередность и социальные правила", "type": "interact", "target": "circular_mate_ritual", "count": 1, "optional": false}, {"id": "make_sharing_choice", "text": "Сделать выбор — поделиться напитком с NPC или сохранить личное употребление, влияя на репутацию", "type": "choice", "target": "mate_sharing_choice", "count": 1, "optional": false}], "rewards": {"xp": 800, "currency": -15, "items": [], "reputation": {"social": 15}, "achievements": [{"id": "matero", "name": "Матеро"}], "skill_boosts": {"energy": 15}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит с традицией мате, где игрок приобретает калабасу и bombilla, учится правильной заварке и принимает\\nучастие в коллективном ритуале. Социальное взаимодействие формирует доверие жителей и открывает доступ к локальным\\nсобытиям районов Буэнос-Айреса.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "cultural_roots", "title": "Региональные отсылки", "body": "Матe — национальный напиток Аргентины. Калабаса изготавливается из высушенной тыквы, bombilla фильтрует напиток, а\\nгорький вкус и кофеин служат стимулом для общения. Передача чаши по кругу символизирует доверие и равенство.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Приобрести набор калабаса + bombilla + yerba mate у локального торговца.\\n2. Пройти урок подготовки напитка: заполнить калабасу, залить горячей (но не кипящей) водой, правильно разместить bombilla.\\n3. Участвовать в круговом ритуале, соблюдая очередность и социальные правила.\\n4. Сделать выбор — поделиться напитком с NPC или сохранить личное употребление, влияя на репутацию и экономику района.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение квеста приносит 800 опыта, временный бафф «Energy +15%» на 6 часов и ачивку «Матеро». Расходы составляют\\n15 единиц валюты на покупку набора.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Социальный сервис отслеживает доверие среди NPC, economy-service фиксирует рост продаж yerba mate, а narrative-service\\nоткрывает дополнительные события в культурной линии Буэнос-Айреса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140891756, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конвертирован квест «Культура мате» в структурированный YAML и зафиксированы игровые интеграции."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

