INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-houston-2029-tex-mex-breakfast', 'Хьюстон 2020-2029 — Tex-Mex завтрак', 'Игрок посещает закусочную, собирает вариации Tex-Mex завтрака и получает временный бафф после дегустации.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_tex_mex_diner", "text": "Найти популярную Tex-Mex закусочную и договориться о дегустации завтрака", "type": "interact", "target": "tex_mex_diner", "count": 1, "optional": false}, {"id": "collect_tacos", "text": "Собрать три варианта breakfast tacos с яйцами, чоризо, картофелем и беконом", "type": "interact", "target": "breakfast_tacos", "count": 3, "optional": false}, {"id": "customize_salsa", "text": "Настроить уровень остроты сальсы и сравнить вкусы с завсегдатаями", "type": "interact", "target": "salsa_customization", "count": 1, "optional": false}, {"id": "document_experience", "text": "Зафиксировать впечатления для гастрономического блога и получить отзывы NPC", "type": "interact", "target": "food_blog", "count": 1, "optional": false}, {"id": "complete_mission", "text": "Завершить миссию, получив энергетический бафф и рекомендацию новых точек", "type": "interact", "target": "mission_completion", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": -10, "items": [{"item_id": "achievement_tex_mex_breakfast_master", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "tex_mex_network_access"]}'::jsonb, '[{"branch_id": "master_breakfast", "description": "Стать мастером Tex-Mex завтраков", "consequences": {"skills": {"culinary": 25}, "reputation": {"Tex_Mex_Restaurants": 20}, "unlocks": ["exclusive_breakfast_spots"]}}]'::jsonb, '{"metadata": {"id": "quest-houston-2029-tex-mex-breakfast", "title": "Хьюстон 2020-2029 — Tex-Mex завтрак", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["houston", "tex-mex", "breakfast"], "topics": ["culinary_culture", "slice_of_life"], "related_systems": ["narrative-service", "economy-service", "social-service", "gameplay-service", "quest-service", "character-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1151", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1151", "relation": "migrated_to", "migrated_at": "2025-11-23T03:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/houston/2020-2029/quest-009-tex-mex-breakfast.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линейке Хьюстона требовалась повседневная гастрономическая миссия, подчёркивающая Tex-Mex идентичность города.", "goal": "Погрузить игрока в ритуал утренних breakfast tacos и объяснить, как блюдо усиливает энергетику персонажа.", "essence": "Игрок посещает закусочную, собирает вариации Tex-Mex завтрака и получает временный бафф после дегустации.", "key_points": ["Показываются традиционные ингредиенты и масштабы порций в хьюстонских diner.", "Этапы фиксируют общение с поварами и выбор остроты сальсы.", "Награды дают ощутимый энергетический бонус и репутацию в гастрономической сети."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_tex_mex_diner", "text": "Найти популярную Tex-Mex закусочную и договориться о дегустации завтрака", "type": "interact", "target": "tex_mex_diner", "count": 1, "optional": false}, {"id": "collect_tacos", "text": "Собрать три варианта breakfast tacos с яйцами, чоризо, картофелем и беконом", "type": "interact", "target": "breakfast_tacos", "count": 3, "optional": false}, {"id": "customize_salsa", "text": "Настроить уровень остроты сальсы и сравнить вкусы с завсегдатаями", "type": "interact", "target": "salsa_customization", "count": 1, "optional": false}, {"id": "document_experience", "text": "Зафиксировать впечатления для гастрономического блога и получить отзывы NPC", "type": "interact", "target": "food_blog", "count": 1, "optional": false}, {"id": "complete_mission", "text": "Завершить миссию, получив энергетический бафф и рекомендацию новых точек", "type": "interact", "target": "mission_completion", "count": 1, "optional": false}], "rewards": {"experience": 600, "money": -10, "items": [{"item_id": "achievement_tex_mex_breakfast_master", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "tex_mex_network_access"]}, "branches": [{"branch_id": "master_breakfast", "description": "Стать мастером Tex-Mex завтраков", "consequences": {"skills": {"culinary": 25}, "reputation": {"Tex_Mex_Restaurants": 20}, "unlocks": ["exclusive_breakfast_spots"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в ранний часов пояс Хьюстона, где Tex-Mex breakfast tacos считаются базовым ритуалом и\\nслужат социальным клеем для рабочих районов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Найти популярную Tex-Mex закусочную и договориться о дегустации завтрака.\\n2. Собрать три варианта breakfast tacos с яйцами, чоризо, картофелем и беконом.\\n3. Настроить уровень остроты сальсы и сравнить вкусы с завсегдатаями.\\n4. Зафиксировать впечатления для гастрономического блога и получить отзывы NPC.\\n5. Завершить миссию, получив энергетический бафф и рекомендацию новых точек.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Tex-Mex breakfast tacos распространены по всему Большому Хьюстону.\\n- Семейные рецепты передаются через поколения и задают тон локальной кухне.\\n- Острая сальса утром считается нормой и символом бодрого начала дня.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 600 XP.\\n- Финансы: -10 еддис.\\n- Бафф «Энергия» +20% на 8 часов.\\n- Ачивка «Tex-Mex Breakfast мастер».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Кулинарные баффы, влияющие на характеристики персонажа.\\n- Социальные диалоги с NPC и сбор отзывов.\\n- Экономика локальных заведений и стимул повторных визитов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест ведёт к гастрономическим маршрутам Техаса и открывает события фестивалей Tex-Mex в Далласе и Сан-Антонио.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1151, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики кулинарного квеста, баффы от еды и гастрономической сети."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Tex-Mex завтрак» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

