INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-houston-2029-medical-center', 'Хьюстон 2020-2029 — Техасский медицинский центр', 'Игрок посещает кампус, знакомится с ключевыми госпиталями и получает доступ к экспертам MD Anderson.', 'main', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_tour_permission", "text": "Получить разрешение на экскурсию и встретиться с координатором кампуса", "type": "interact", "target": "campus_coordinator", "count": 1, "optional": false}, {"id": "explore_central_buildings", "text": "Осмотреть центральные корпуса и понять логистику 21 госпиталя", "type": "explore", "target": "medical_center_buildings", "count": 1, "optional": false}, {"id": "visit_md_anderson", "text": "Посетить MD Anderson Cancer Center и изучить инновационные подходы к лечению", "type": "interact", "target": "md_anderson_center", "count": 1, "optional": false}, {"id": "analyze_statistics", "text": "Проанализировать статистику: 106 000 сотрудников и 8 млн пациентов ежегодно", "type": "investigate", "target": "medical_center_stats", "count": 1, "optional": false}, {"id": "prepare_report", "text": "Подготовить отчёт для фракции, выделив потенциал сотрудничества и риски", "type": "interact", "target": "medical_center_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 2500, "money": 0, "items": [{"item_id": "achievement_medical_expert", "quantity": 1}], "reputation": {}, "unlocks": ["medical_quests", "healthcare_resources_access"]}'::jsonb, '[{"branch_id": "establish_partnership", "description": "Установить партнёрство с медицинским центром", "consequences": {"reputation": {"Medical_Professionals": 30, "Research_Institutes": 25}, "unlocks": ["advanced_medical_access"]}}]'::jsonb, '{"metadata": {"id": "quest-houston-2029-medical-center", "title": "Хьюстон 2020-2029 — Техасский медицинский центр", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["houston", "medical", "infrastructure"], "topics": ["healthcare", "science"], "related_systems": ["narrative-service", "wellbeing-service", "gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1152", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1152", "relation": "migrated_to", "migrated_at": "2025-11-23T03:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/houston/2020-2029/quest-010-medical-center.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Медицинская линия Хьюстона требовала структурированного описания Texas Medical Center как мировой столицы медицины.", "goal": "Показать масштаб центра, его учреждения и влияние на региональные фракции.", "essence": "Игрок посещает кампус, знакомится с ключевыми госпиталями и получает доступ к экспертам MD Anderson.", "key_points": ["Texas Medical Center — крупнейший медицинский комплекс мира.", "В инфраструктуре более 106 000 сотрудников, 21 госпиталь и 8 млн пациентов в год.", "Квест усиливает репутацию в научно-медицинских фракциях."]}, "quest_definition": {"quest_type": "main", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_tour_permission", "text": "Получить разрешение на экскурсию и встретиться с координатором кампуса", "type": "interact", "target": "campus_coordinator", "count": 1, "optional": false}, {"id": "explore_central_buildings", "text": "Осмотреть центральные корпуса и понять логистику 21 госпиталя", "type": "explore", "target": "medical_center_buildings", "count": 1, "optional": false}, {"id": "visit_md_anderson", "text": "Посетить MD Anderson Cancer Center и изучить инновационные подходы к лечению", "type": "interact", "target": "md_anderson_center", "count": 1, "optional": false}, {"id": "analyze_statistics", "text": "Проанализировать статистику: 106 000 сотрудников и 8 млн пациентов ежегодно", "type": "investigate", "target": "medical_center_stats", "count": 1, "optional": false}, {"id": "prepare_report", "text": "Подготовить отчёт для фракции, выделив потенциал сотрудничества и риски", "type": "interact", "target": "medical_center_report", "count": 1, "optional": false}], "rewards": {"experience": 2500, "money": 0, "items": [{"item_id": "achievement_medical_expert", "quantity": 1}], "reputation": {}, "unlocks": ["medical_quests", "healthcare_resources_access"]}, "branches": [{"branch_id": "establish_partnership", "description": "Установить партнёрство с медицинским центром", "consequences": {"reputation": {"Medical_Professionals": 30, "Research_Institutes": 25}, "unlocks": ["advanced_medical_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с Texas Medical Center, демонстрируя масштабы кампуса, уникальные клиники и значение для города.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить разрешение на экскурсию и встретиться с координатором кампуса.\\n2. Осмотреть центральные корпуса и понять логистику 21 госпиталя.\\n3. Посетить MD Anderson Cancer Center и изучить инновационные подходы к лечению.\\n4. Проанализировать статистику: 106 000 сотрудников и 8 млн пациентов ежегодно.\\n5. Подготовить отчёт для фракции, выделив потенциал сотрудничества и риски.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Texas Medical Center объединяет 21 госпиталь, исследовательские институты и университеты. MD Anderson признан одним из\\nлучших онкоцентров мира. Комплекс ведёт круглосуточную работу и считается «городом в городе».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP\\n- Финансы: 0\\n- Атрибут «Медицина»: +25\\n- Ачивка: «Знаток Медицины»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует образовательные и инфраструктурные механики, добавляет доступ к медицинским ресурсам и возможные\\nбонусы к лечению союзников.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с медицинской линией Хьюстона и глобальными сюжетами здравоохранения, может вести к заданиям по биотеху и\\nэвакуации пациентов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1152, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики медицинского квеста, интеграция с медицинскими учреждениями и научными исследованиями."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Техасский медицинский центр» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = 