INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hk-dim-sum-masters', 'Гонконг 2020-2029 — Мастера дим-сама', 'Игрок посещает Tim Ho Wan, Maxim''s Palace и Lung King Heen, дегустирует блюда и выбирает победителя.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hk-dim-sum-masters", "title": "Гонконг 2020-2029 — Мастера дим-сама", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:20:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["hong_kong", "dim_sum", "cuisine"], "topics": ["culinary-tour", "cantonese-culture"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "reputation-service"], "related_documents": [{"id": "github-issue-381", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/381", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-004-dim-sum-masters.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Тур по дим-саму оставался в Markdown и не взаимодействовал с системой гастрономических баффов.", "goal": "Описать охоту за лучшим дим-самом Гонконга в YAML, выделив ключевые рестораны и эффекты.", "essence": "Игрок посещает Tim Ho Wan, Maxim''s Palace и Lung King Heen, дегустирует блюда и выбирает победителя.", "key_points": ["Квест-id HK-2029-004, сложность medium, длительность 2–4 часа.", "Сценарий охватывает культуру yum cha, тележки с дим-самом и мишленовские заведения.", "Награда даёт 2 000 XP, +30% HP на 12 часов и титул «Dim Sum Master»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Гонконг — мировая столица дим-сама, где визит в чайные дома становится соревнованием за звание лучшего мастера кулинарии.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Опросить местных жителей о культовых заведениях.\\n2. Посетить Tim Ho Wan, Maxim''s Palace и Lung King Heen, протестировав разнообразие har gow, siu mai, char siu bao.\\n3. Сравнить сервис, вкусы и атмосферу, оформить рейтинговый отчёт.\\n4. Назначить победителя и подготовить рекомендации для туристического гида.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны традиции yum cha, тележки с паровыми корзинами, шум кантонских залов и роль бесконечного подливаемого чая.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 200 HKD на дегустацию, получает 2 000 XP, +30% HP на 12 часов и титул «Dim Sum Master», повышая репутацию среди кулинарных фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 381, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Мастера дим-сама» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

