INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vegas-2029-cirque-du-soleil', 'Лас-Вегас 2020-2029 — Cirque du Soleil', 'Игрок выбирает одно из семи постоянных шоу, наблюдает акробатику мирового класса и знакомится с закулисьем.', 'side', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "choose_show", "text": "Выбрать одно из семи постоянных шоу Cirque du Soleil", "type": "choice", "target": "cirque_show_selection", "count": 1, "optional": false}, {"id": "buy_tickets", "text": "Приобрести билеты на шоу ($100-300)", "type": "interact", "target": "cirque_box_office", "count": 1, "optional": false}, {"id": "watch_performance", "text": "Наблюдать акробатику мирового класса", "type": "interact", "target": "cirque_show_performance", "count": 1, "optional": false}, {"id": "meet_backstage", "text": "Посетить закулисье и познакомиться с артистами", "type": "interact", "target": "cirque_backstage", "count": 1, "optional": true}]'::jsonb, '{"xp": 2500, "currency": 0, "attributes": {"culture": 15}, "achievements": [{"id": "cirque_visitor", "name": "Посетитель Cirque du Soleil"}], "reputation": {"elite": 50}, "items": []}'::jsonb, '[{"condition": "VIP-билеты и закулисье", "outcome": "Дополнительная репутация и доступ к элитным контактам", "next_quests": []}]'::jsonb, '{"metadata": {"id": "quest-vegas-2029-cirque-du-soleil", "title": "Лас-Вегас 2020-2029 — Cirque du Soleil", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["las-vegas", "entertainment", "cirque-du-soleil"], "topics": ["performing_arts", "premium_events"], "related_systems": ["narrative-service", "liveops-service", "gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/las-vegas/2020-2029/quest-005-cirque-du-soleil.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Развлекательная линейка Вегаса требовала описания флагманских шоу Cirque du Soleil.", "goal": "Зафиксировать опыт посещения премиального спектакля и его влияние на культурный статус игрока.", "essence": "Игрок выбирает одно из семи постоянных шоу, наблюдает акробатику мирового класса и знакомится с закулисьем.", "key_points": ["Cirque du Soleil держит семь резидентских постановок в Вегасе, включая водное шоу «O».", "Билеты стоят $100–300 и обеспечивают доступ к лучшим артистам мира.", "Квест усиливает культурную репутацию и открывает элитные контакты."]}, "quest_definition": {"quest_type": "side", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "choose_show", "text": "Выбрать одно из семи постоянных шоу Cirque du Soleil", "type": "choice", "target": "cirque_show_selection", "count": 1, "optional": false}, {"id": "buy_tickets", "text": "Приобрести билеты на шоу ($100-300)", "type": "interact", "target": "cirque_box_office", "count": 1, "optional": false}, {"id": "watch_performance", "text": "Наблюдать акробатику мирового класса", "type": "interact", "target": "cirque_show_performance", "count": 1, "optional": false}, {"id": "meet_backstage", "text": "Посетить закулисье и познакомиться с артистами", "type": "interact", "target": "cirque_backstage", "count": 1, "optional": true}], "rewards": {"xp": 2500, "currency": 0, "attributes": {"culture": 15}, "achievements": [{"id": "cirque_visitor", "name": "Посетитель Cirque du Soleil"}], "reputation": {"elite": 50}, "items": []}, "branches": [{"condition": "VIP-билеты и закулисье", "outcome": "Дополнительная репутация и доступ к элитным контактам", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает игрока в атмосферу Cirque du Soleil, подчёркивая уникальные постановки Вегаса и техническое\\nсовершенство шоу.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Выбрать спектакль среди семи резидентских шоу (например, «O», «Mystère») и приобрести билеты.\\n2. Посетить театр и ознакомиться с предсценарием о создании постановки.\\n3. Наблюдать основное шоу: акробатика, синхронное плавание, световые эффекты.\\n4. Получить доступ к закулисью или цифровому архиву, чтобы изучить работу труппы.\\n5. Подготовить культурный отчёт для туристической фракции.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "В Лас-Вегасе Cirque du Soleil представлен семью постановками, включая водное шоу «O» с бассейном на сцене.\\nАкробаты мирового уровня и сложные технические решения сделали спектакли визитной карточкой города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP\\n- Финансы: -200 единиц валюты\\n- Атрибут «Культура»: +25\\n- Ачивка: «Ценитель Цирка»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует зрелищные механики, премиальные расходы и социальные бонусы, позволяя повысить авторитет среди\\nкультурных кругов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с развлекательной линией Вегаса и событиями премиум-класса, может вести к заданиям о театральных труппах\\nи спонсорстве.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1137, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Cirque du Soleil» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

