INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vegas-2029-neon-boneyard', 'Лас-Вегас 2020-2029 — Кладбище неона', 'Игрок исследует коллекцию из более чем 200 вывесок, участвует в ночном туре и собирает устные легенды старого Вегаса.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_neon_museum", "text": "Посетить Neon Museum и изучить коллекцию из 200+ вывесок", "type": "interact", "target": "neon_museum", "count": 1, "optional": false}, {"id": "identify_signs", "text": "Узнать вывески закрытых казино (Stardust, Moulin Rouge, Desert Inn)", "type": "interact", "target": "historic_neon_signs", "count": 3, "optional": false}, {"id": "night_tour", "text": "Принять участие в ночном туре с оживлением экспонатов", "type": "interact", "target": "neon_night_tour", "count": 1, "optional": false}, {"id": "collect_stories", "text": "Собрать устные легенды старого Вегаса", "type": "interact", "target": "vegas_oral_legends", "count": 1, "optional": false}]'::jsonb, '{"xp": 1800, "currency": 0, "attributes": {"history": 10}, "achievements": [{"id": "neon_historian", "name": "Историк неонового Вегаса"}], "reputation": {"vegas_history": 60}, "items": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vegas-2029-neon-boneyard", "title": "Лас-Вегас 2020-2029 — Кладбище неона", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["las-vegas", "neon", "museum"], "topics": ["cultural_heritage", "nostalgia"], "related_systems": ["narrative-service", "education-service", "gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/las-vegas/2020-2029/quest-006-neon-boneyard.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая линия Вегаса требовала квеста о сохранении неонового наследия города.", "goal": "Представить Neon Museum как окно в прошлое, показать ключевые вывески и их истории.", "essence": "Игрок исследует коллекцию из более чем 200 вывесок, участвует в ночном туре и собирает устные легенды старого Вегаса.", "key_points": ["Neon Boneyard содержит вывески закрытых казино (Stardust, Moulin Rouge, Desert Inn).", "Ночные туры оживляют часть экспонатов, создавая ностальгический эффект.", "Квест усиливает историческую репутацию и доступ к архивам города."]}, "quest_definition": {"quest_type": "side", "level_min": 4, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_neon_museum", "text": "Посетить Neon Museum и изучить коллекцию из 200+ вывесок", "type": "interact", "target": "neon_museum", "count": 1, "optional": false}, {"id": "identify_signs", "text": "Узнать вывески закрытых казино (Stardust, Moulin Rouge, Desert Inn)", "type": "interact", "target": "historic_neon_signs", "count": 3, "optional": false}, {"id": "night_tour", "text": "Принять участие в ночном туре с оживлением экспонатов", "type": "interact", "target": "neon_night_tour", "count": 1, "optional": false}, {"id": "collect_stories", "text": "Собрать устные легенды старого Вегаса", "type": "interact", "target": "vegas_oral_legends", "count": 1, "optional": false}], "rewards": {"xp": 1800, "currency": 0, "attributes": {"history": 10}, "achievements": [{"id": "neon_historian", "name": "Историк неонового Вегаса"}], "reputation": {"vegas_history": 60}, "items": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в Neon Museum, где он изучает эволюцию неоновой эстетики и влияние вывесок на образ Вегаса.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть в Neon Museum и пройти вводный брифинг куратора.\\n2. Осмотреть ключевые вывески (Stardust, Moulin Rouge, Desert Inn), изучив их историю.\\n3. Участвовать в ночном туре, наблюдая восстановленные неоновые огни.\\n4. Собрать истории ветеранов индустрии о расцвете и падении казино.\\n5. Подготовить музейный отчёт с рекомендациями по сохранению экспонатов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Neon Museum (Neon Boneyard) вмещает более 200 вывесок, рассказывающих историю Вегаса. Некоторые, как вывеска\\nStardust, стали символами золотой эры и восстановлены для ночных туров.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 200 XP\\n- Финансы: -20 единиц валюты\\n- Атрибут «История»: +15\\n- Ачивка: «Археолог Неона»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует исторические и музейные механики, добавляет бонусы ностальгии и доступ к архивным источникам.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с исторической линией Вегаса и квестами сохранения культурного наследия, может вести к миссиям реставраторов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1138, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Кладбище неона» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

