INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hk-mong-kok-density', 'Гонконг 2020-2029 — Плотность Монг Кока', 'Игрок пробирается через Монг Кок в час-пик, посещает рынки и управляет ресурсами стресса и концентрации.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hk-mong-kok-density", "title": "Гонконг 2020-2029 — Плотность Монг Кока", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:25:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["hong_kong", "mong_kok", "density"], "topics": ["crowd-management", "market-life"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "survival-service", "social-service", "market-system"], "related_documents": [{"id": "github-issue-382", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/382", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-008-mong-kok-density.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий «Плотность Монг Кока» находился в Markdown и не моделировал толпы и рынки в системах.", "goal": "Структурировать опыт выживания в районе с рекордной плотностью населения в YAML.", "essence": "Игрок пробирается через Монг Кок в час-пик, посещает рынки и управляет ресурсами стресса и концентрации.", "key_points": ["Квест-id HK-2029-008, сложность hard, длительность 2–4 часа.", "Этапы включают Ladies'' Market, Goldfish Market и Bird Market.", "Награда выдаёт 2 000 XP, повышение выносливости и титул «Король плотности»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Монг Кок — «оживлённый угол» Гонконга, территория с плотностью до 130 000 человек на км², где неон, запахи и толпы создают уникальное напряжение.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Войти в район в час-пик и адаптироваться к потоку людей.\\n2. Пробраться через Ladies'' Market, балансируя покупки и ограничения веса.\\n3. Посетить Goldfish Market и Bird Garden, фиксируя культурные особенности.\\n4. Провести эвакуацию из района, не потерявшись и сохранив ресурсы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны уровни плотности, запахи еды и благовоний, неоновые вывески, вертикальные дома и постоянное движение.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 500 HKD на покупки, получает 2 000 XP, усиление выносливости и титул «Король плотности», открывая бонусы к навигации в толпе.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 382, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Плотность Монг Кока» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

