INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-boston-cyber-security-research', 'Boston 2020-2029 — «Исследования кибербезопасности»', 'Бостон как центр академических инноваций в области кибербезопасности, где образование встречается с высокими технологиями.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-boston-cyber-security-research", "title": "Boston 2020-2029 — «Исследования кибербезопасности»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:51:56+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["boston", "cyber-security", "research", "education", "hacking"], "topics": ["security", "education", "technology"], "related_systems": ["narrative-service", "security-system", "education-system"], "related_documents": [{"id": "canon-region-america-boston-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-012-cyber-security-research.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "MIT и Harvard запускают совместную программу по кибербезопасности, но корпоративные шпионы пытаются украсть исследования.", "goal": "Игрок участвует в программе как тестер, помогая защитить инновационные разработки от промышленного шпионажа.", "essence": "Бостон как центр академических инноваций в области кибербезопасности, где образование встречается с высокими технологиями.", "key_points": ["Совместные исследования MIT и Harvard по квантовой криптографии.", "Корпоративный шпионаж от Arasaka и Militech.", "Финальная защита прототипа квантового шифровальщика."]}, "content": {"sections": [{"id": "research_partnership", "title": "Партнерство исследований", "body": "Игрок получает приглашение от MIT для участия в программе тестирования систем кибербезопасности. Знакомство с учеными из Harvard.\\n", "mechanics_links": [], "assets": []}, {"id": "corporate_espionage", "title": "Корпоративный шпионаж", "body": "Обнаруживаются попытки взлома университетских серверов. Игрок начинает расследование корпоративного шпионажа.\\n", "mechanics_links": [], "assets": []}, {"id": "quantum_encryption", "title": "Квантовая криптография", "body": "Мини-игра: тестирование квантовых алгоритмов шифрования. Игрок должен предотвратить взлом прототипа системы.\\n", "mechanics_links": [], "assets": []}, {"id": "security_conference", "title": "Конференция безопасности", "body": "Финальная презентация результатов на международной конференции. Игрок защищает демонстрацию от диверсантов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1000 XP.\\n- Издержки: −45 кредов.\\n- Бафф «Кибер-защитник +25% эффективности в хакерских задачах на 16 часов».\\n- Ачивка «Академический страж».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Quantum Encryption", "definition": "Криптографические методы, использующие принципы квантовой физики для абсолютной защиты данных."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875688, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Исследования кибербезопасности» для Boston 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED