INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-traffic-chaos', 'Дели 2020-2029 — Транспортный хаос', 'Игрок выживает в потоке машин, рикш и коров, обучаясь местным правилам и языку сигналов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-traffic-chaos", "title": "Дели 2020-2029 — Транспортный хаос", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:10:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["delhi", "traffic", "survival"], "topics": ["transportation", "urban-life"], "related_systems": ["narrative-service", "transportation-system", "stress-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/delhi/2020-2029/quest-004-traffic-chaos.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о дорожном хаосе Дели оставался в Markdown и не описывал системные эффекты транспорта и торга.", "goal": "Перенести сценарий в YAML с акцентом на авто-рикши, переговоры и адаптацию к движению.", "essence": "Игрок выживает в потоке машин, рикш и коров, обучаясь местным правилам и языку сигналов.", "key_points": ["Квест-id DELHI-2029-004, сложность hard, длительность 1–2 часа.", "Этапы включают пересечение дороги, поездку на рикше и переговоры о цене.", "Награда выдаёт 2 000 XP, +10 к выносливости и титул «Повелитель хаоса»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Дорожная экосистема Дели — это симфония гудков, авто-рикш, коров и двухколёсных транспортов, требующая быстрой адаптации.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Попробовать пересечь оживлённую дорогу, учитывая хаотичное движение.\\n2. Нанять авто-рикшу, сторговавшись о цене и инструктируя водителя по маршруту.\\n3. Пройти через пробки, избегая столкновений и священных коров.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указаны плотность населения, роль авто-рикш, культурное значение коров и значение гудков как языка дороги.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 5 едди, получает 2 000 XP, +10 к выносливости и титул «Повелитель хаоса», открывая новые транспортные маршруты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 256, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Транспортный хаос» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.br