INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vegas-2029-desert-heat', 'Лас-Вегас 2020-2029 — Жара пустыни', 'Игрок сталкивается с экстремальной температурой, контролирует гидратацию и оценивает роль кондиционирования.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "experience_extreme_heat", "text": "Столкнуться с экстремальной температурой (+47°C летом)", "type": "interact", "target": "desert_heat_zone", "count": 1, "optional": false}, {"id": "monitor_hydration", "text": "Контролировать гидратацию и избегать обезвоживания", "type": "interact", "target": "hydration_management", "count": 1, "optional": false}, {"id": "find_shade_ac", "text": "Найти укрытие с кондиционером для отдыха", "type": "interact", "target": "ac_shelter", "count": 1, "optional": false}, {"id": "learn_survival_strategies", "text": "Изучить стратегии выживания в пустынной жаре", "type": "interact", "target": "desert_survival_guide", "count": 1, "optional": false}]'::jsonb, '{"xp": 1500, "currency": 0, "attributes": {"endurance": 10}, "achievements": [{"id": "desert_survivor", "name": "Выживший в пустыне"}], "reputation": {"vegas_local": 30}, "items": []}'::jsonb, '[{"condition": "Успешное выживание без дебаффов", "outcome": "Бонус к выносливости и репутации", "next_quests": []}, {"condition": "Получен дебафф обезвоживания", "outcome": "Меньшее вознаграждение, требуется восстановление", "next_quests": []}]'::jsonb, '{"metadata": {"id": "quest-vegas-2029-desert-heat", "title": "Лас-Вегас 2020-2029 — Жара пустыни", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["las-vegas", "climate", "survival"], "topics": ["environment", "physiology"], "related_systems": ["narrative-service", "survival-service", "gameplay-service", "quest-service", "character-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/las-vegas/2020-2029/quest-009-desert-heat.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Климатическая линия Вегаса требовала сценария, демонстрирующего экстремальную жару пустыни Мохаве.", "goal": "Зафиксировать опыт выживания при +47 °C и влияние жары на повседневность жителей.", "essence": "Игрок сталкивается с экстремальной температурой, контролирует гидратацию и оценивает роль кондиционирования.", "key_points": ["Летом температура достигает +47 °C, а асфальт прогревается до +70 °C.", "Сухая жара обезвоживает, без кондиционеров жизнь невозможна.", "Квест вводит дебаффы на выносливость и обучает стратегиям выживания."]}, "quest_definition": {"quest_type": "side", "level_min": 4, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "experience_extreme_heat", "text": "Столкнуться с экстремальной температурой (+47°C летом)", "type": "interact", "target": "desert_heat_zone", "count": 1, "optional": false}, {"id": "monitor_hydration", "text": "Контролировать гидратацию и избегать обезвоживания", "type": "interact", "target": "hydration_management", "count": 1, "optional": false}, {"id": "find_shade_ac", "text": "Найти укрытие с кондиционером для отдыха", "type": "interact", "target": "ac_shelter", "count": 1, "optional": false}, {"id": "learn_survival_strategies", "text": "Изучить стратегии выживания в пустынной жаре", "type": "interact", "target": "desert_survival_guide", "count": 1, "optional": false}], "rewards": {"xp": 1500, "currency": 0, "attributes": {"endurance": 10}, "achievements": [{"id": "desert_survivor", "name": "Выживший в пустыне"}], "reputation": {"vegas_local": 30}, "items": []}, "branches": [{"condition": "Успешное выживание без дебаффов", "outcome": "Бонус к выносливости и репутации", "next_quests": []}, {"condition": "Получен дебафф обезвоживания", "outcome": "Меньшее вознаграждение, требуется восстановление", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест моделирует день в середине лета, когда Лас-Вегас и пустыня Мохаве испытывают экстремальную жару, требующую\\nстрогого контроля ресурсов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Выйти на улицу в июле при пиковой температуре и провести базовые измерения.\\n2. Столкнуться с эффектами сухой жары: обезвоживание, перегрев, замедление реакции.\\n3. Протестировать защитные средства (головные уборы, охлаждающие пакеты, воду).\\n4. Ночью оценить, что температура остаётся выше +35 °C и нагрузка сохраняется.\\n5. Подготовить рекомендации для жителей и туристов по выживанию в жару.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Лас-Вегас расположен в пустыне Мохаве: летом температура достигает +47 °C, а асфальт нагревается до +70 °C.\\nГород зависит от кондиционирования и инфраструктуры охлаждения, иначе пребывание становится смертельно опасным.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP\\n- Финансы: 0\\n- Атрибут «Выносливость»: +20\\n- Дебафф: -30% Stamina при нахождении под солнцем без защиты\\n- Ачивка: «Выживший в пустыне»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест задействует климатические и физиологические механики, учитывает обезвоживание, тепловой удар и необходимость\\nстратегического планирования времени активности.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с климатической линией Вегаса, влияет на эффективность других квестов, происходящих в дневное время.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1141, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Жара пустыни» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

