INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-las-vegas-excess-experience-2020-2029', 'Лас-Вегас: «What happens in Vegas…»', 'Команда друзей прилетает в Вегас, обходит казино, клубы и вечеринки, а финал оставляет события в секрете.', 'side', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "gather_friends", "text": "Собрать команду друзей для группового опыта", "type": "interact", "target": "friends_gathering", "count": 1, "optional": false}, {"id": "visit_casinos", "text": "Обойти несколько казино и попробовать разные игры", "type": "interact", "target": "multiple_casinos", "count": 3, "optional": false}, {"id": "attend_nightclubs", "text": "Посетить ночные клубы и вечеринки", "type": "interact", "target": "nightclub_venues", "count": 2, "optional": false}, {"id": "join_pool_party", "text": "Участвовать в дневной pool-party", "type": "interact", "target": "pool_party_event", "count": 1, "optional": false}, {"id": "complete_24h_marathon", "text": "Завершить 24-часовой марафон развлечений", "type": "interact", "target": "vegas_24h_marathon", "count": 1, "optional": false}]'::jsonb, '{"xp": 3000, "currency": 0, "attributes": {"party": 20}, "achievements": [{"id": "vegas_legend", "name": "Легенда Вегаса"}], "reputation": {"social": 100}, "items": [{"id": "vegas_memories", "name": "Воспоминания Вегаса", "quantity": 1}]}'::jsonb, '[{"condition": "Все события остаются в секрете", "outcome": "Уникальное достижение и максимальная репутация", "next_quests": []}]'::jsonb, '{"metadata": {"id": "canon-quest-las-vegas-excess-experience-2020-2029", "title": "Лас-Вегас: «What happens in Vegas…»", "document_type": "canon", "category": "quests", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["las-vegas", "nightlife", "quest"], "topics": ["social_experience", "hospitality", "quests"], "related_systems": ["narrative-service", "quest-service", "world-simulation", "social-service", "gameplay-service", "character-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/las-vegas/2020-2029/quest-010-what-happens-in-vegas.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальная линия Лас-Вегаса требовала сценария о групповом безудержном опыте «What happens in Vegas».", "goal": "Погрузить игроков в 24-часовой марафон развлечений, демонстрирующий избыточность и анонимность города.", "essence": "Команда друзей прилетает в Вегас, обходит казино, клубы и вечеринки, а финал оставляет события в секрете.", "key_points": ["Слоган «What happens in Vegas, stays in Vegas» становится нарративной рамкой.", "Геймплей чередует азартные игры, ночные клубы и дневные pool-party.", "Награды компенсируют риски высокой траты валюты уникальными воспоминаниями и достижением."]}, "quest_definition": {"quest_type": "side", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "gather_friends", "text": "Собрать команду друзей для группового опыта", "type": "interact", "target": "friends_gathering", "count": 1, "optional": false}, {"id": "visit_casinos", "text": "Обойти несколько казино и попробовать разные игры", "type": "interact", "target": "multiple_casinos", "count": 3, "optional": false}, {"id": "attend_nightclubs", "text": "Посетить ночные клубы и вечеринки", "type": "interact", "target": "nightclub_venues", "count": 2, "optional": false}, {"id": "join_pool_party", "text": "Участвовать в дневной pool-party", "type": "interact", "target": "pool_party_event", "count": 1, "optional": false}, {"id": "complete_24h_marathon", "text": "Завершить 24-часовой марафон развлечений", "type": "interact", "target": "vegas_24h_marathon", "count": 1, "optional": false}], "rewards": {"xp": 3000, "currency": 0, "attributes": {"party": 20}, "achievements": [{"id": "vegas_legend", "name": "Легенда Вегаса"}], "reputation": {"social": 100}, "items": [{"id": "vegas_memories", "name": "Воспоминания Вегаса", "quantity": 1}]}, "branches": [{"condition": "Все события остаются в секрете", "outcome": "Уникальное достижение и максимальная репутация", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игроки прибывают в Лас-Вегас группой, чтобы прожить легендарные 24 часа без ограничений. Квест комбинирует\\nазартные игры, танцевальные клубы, бассейн-вечеринки и шоу, подчёркивая образ города «излишеств».\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Прилет в Лас-Вегас и заселение в отель-казино, брифинг по правилам «stay in Vegas».\\n2. Ночная сессия в казино: блэкджек, рулетка, VIP-комнаты.\\n3. Серия клубов и шоу до рассвета, взаимодействия с NPC-partygoers.\\n4. Дневные pool-party и бар-хоппинг по Стрипу.\\n5. Финальная сцена с провалом памяти и фиксацией последствий для команды.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Официальный слоган города подчёркивает анонимность и свободу поведения.\\n- Bachelor/bachelorette вечеринки и круглосуточная инфраструктура составляют ядро экономики.\\n- Избыточность проявляется в неоновой архитектуре, гигантских казино и постоянных шоу-программах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 5 000 XP.\\n- Финансовые расходы: от -1 000 до -5 000.\\n- Уникальные воспоминания (нарративные маркеры).\\n- Достижение «Vegas Survivor».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Социальные события в формате кооперативной вечеринки.\\n- Управление рисками при ставках и принятии сомнительных решений.\\n- Память/секретность: часть событий скрыта до последующих сценариев.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает ветки о корпоративных вечеринках Стрипа, VIP-миссии казино и продолжения для отдельных участников\\nгруппы после «забытой» ночи.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1142, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «What happens in Vegas…» с групповой структурой и социальными механиками."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

