INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-pc-bang-champion', 'Сеул 2020-2029 — Чемпион PC Bang', 'Игрок участвует в киберспортивном турнире внутри 24/7 PC Bang, выигрывает финал и становится легендой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-pc-bang-champion", "title": "Сеул 2020-2029 — Чемпион PC Bang", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:30:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "pc_bang", "esports"], "topics": ["competitive-gaming", "youth-culture"], "related_systems": ["narrative-service", "tournament-system", "buff-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-002-pc-bang-champion.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Турнир PC Bang описывался в Markdown и не был привязан к системе киберспорта и ресурсов концентрации.", "goal": "Перевести чемпионат PC Bang в YAML, отразив выбор игры, структуру турнира и награды.", "essence": "Игрок участвует в киберспортивном турнире внутри 24/7 PC Bang, выигрывает финал и становится легендой.", "key_points": ["Квест-id SEOUL-2029-002, сложность hard, длительность 2–4 часа.", "Игрок выбирает StarCraft II или League of Legends, проходит сетку турнира, управляет ресурсами (энергетики, концентрация).", "Награда выдаёт 2 000 XP за участие, 30 000 едди и титул «PC Bang Legend» за победу."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "PC Bang — корейские киберкафе, являющиеся колыбелью киберспорта; они оснащены сотнями ПК, доставкой рамёна и энергетиков прямо за стол.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Зарегистрироваться в 24/7 PC Bang, выбрать дисциплину (SC2 или LoL).\\n2. Пройти групповой этап и полуфинал, управляя концентрацией и перерывами.\\n3. В финале победить местного про-геймера, транслируя матч на внутренние экраны.\\n4. Получить признание зала и записать интервью для киберспортивных СМИ.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны особенности PC Bang: сотни ПК, постоянный шум вентиляторов, доставка рамёна, облака пара вейпов и культ профессиональных игроков.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 2 000 XP за участие, 30 000 едди и титул «PC Bang Legend» за победу, плюс +20 к ресурсу киберспорта.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Чемпион PC Bang» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, 