INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-sacred-cows', 'Дели 2020-2029 — Священные коровы', 'Игрок изучает городские улицы Дели, наблюдая взаимодействие людей и коров, и формирует рекомендации по уважительному поведению.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-sacred-cows", "title": "Дели 2020-2029 — Священные коровы", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:15:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["delhi", "hinduism", "culture"], "topics": ["religious-practices", "urban-life"], "related_systems": ["narrative-service", "reputation-system", "social-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/delhi/2020-2029/quest-007-sacred-cows.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий о священных коровах находился в Markdown и не активировал механики уважения к фракциям.", "goal": "Структурировать наблюдение за коровами в YAML, подчеркнув культурные нормы и социальные эффекты.", "essence": "Игрок изучает городские улицы Дели, наблюдая взаимодействие людей и коров, и формирует рекомендации по уважительному поведению.", "key_points": ["Квест-id DELHI-2029-007, сложность easy, длительность до часа.", "Этапы показывают поведение водителей и жителей, а также религиозный контекст.", "Награда выдаёт 600 XP, +10 к культурному ресурсу и титул «Друг коров»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Индуистская традиция почитает корову как Гау Мату, что делает животных полноправными участниками городской жизни, требующими особого уважения.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Наблюдать за дорожным движением, где коровы свободно передвигаются, а транспорт их объезжает.\\n2. Изучить практику кормления и заботы о коровах горожанами и брахманами.\\n3. Взаимодействовать с местным сообществом, собирая объяснения религиозных норм.\\n4. Сформировать рекомендации по поведению игрока возле священных животных.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны понятия Гау Мата, правовые ограничения на убой, способы того, как коровы становятся частью городской инфраструктуры и религиозных праздников.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 600 XP, +10 к культурному ресурсу и титул «Друг коров», повышая репутацию среди индуистских фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 256, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Священные коровы» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description 