INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-k-pop-idol', 'Сеул 2020-2029 — K-Pop айдол', 'Игрок проходит прослушивание, переживает годы тренировок, дебютирует в группе и управляет славой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-k-pop-idol", "title": "Сеул 2020-2029 — K-Pop айдол", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:30:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "k-pop", "entertainment"], "topics": ["idol-training", "global-pop"], "related_systems": ["narrative-service", "progression-system", "reputation-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-003-k-pop-idol.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Путь K-Pop айдола находился в Markdown и не учитывал систему стажёров, дебюта и управления репутацией.", "goal": "Оформить карьеру K-Pop в YAML, описав этапы стажировки, дебют и мировое турне.", "essence": "Игрок проходит прослушивание, переживает годы тренировок, дебютирует в группе и управляет славой.", "key_points": ["Квест-id SEOUL-2029-003, сложность extreme, длительность 4+ часа.", "Этапы включают прослушивание, стажировку, дебют и мировое турне.", "Награды дают 10 000 XP, +100 славы и высокие доходы при рисках стресса и скандалов."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "K-Pop индустрия создаёт глобальные феномены (BTS, BLACKPINK); стажёры тренируются годами в агентствах Big 3, поддерживая идеальный образ.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Пройти прослушивание в SM, YG или JYP, продемонстрировать вокал, танцы и визуал.\\n2. Прожить годы стажировки с ежедневными тренировками, экзаменами и оценкой ментального здоровья.\\n3. Дебютировать в группе, вести промо-кампании (music shows, variety shows).\\n4. Отправиться в мировое турне, управлять фанатами и расписанием.\\n", "mechanics_links": [], "assets": []}, {"id": "risk_and_management", "title": "Риски и управление", "body": "Указаны ограничения контрактов, запрет на отношения, сталкеры (sasaeng), стресс и необходимость менеджмента репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 10 000 XP, 100 000–1 000 000 едди в месяц, +100 к славе, но сталкивается с рисками выгорания и скандалов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «K-Pop айдол» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

