INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-korean-bbq', 'Сеул 2020-2029 — Корейское барбекю', 'Игрок жарит samgyeopsal на встроенном гриле, делится banchan и завершает вечер караоке.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-korean-bbq", "title": "Сеул 2020-2029 — Корейское барбекю", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:30:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "korean_bbq", "food"], "topics": ["communal-dining", "culinary-experience"], "related_systems": ["narrative-service", "buff-system", "social-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-004-korean-bbq.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о корейском барбекю был в Markdown и не выдавал кулинарных баффов.", "goal": "Структурировать посещение BBQ-ресторана в YAML, подчеркнуть социальную механику.", "essence": "Игрок жарит samgyeopsal на встроенном гриле, делится banchan и завершает вечер караоке.", "key_points": ["Квест-id SEOUL-2029-004, сложность easy, длительность 1–2 часа.", "Этапы показывают приготовление мяса, wrap в салат (ssam) и совместное питьё soju.", "Награда даёт 600 XP, бафф +25% HP на 12 часов и +10 к силе, повышая социализацию."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Korean BBQ — ключевой социальный ритуал в Корее: мясо жарят за столом, окружённые дымом, banchan и смехом друзей.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Собрать группу друзей и занять стол с встроенным грилем.\\n2. Заказать samgyeopsal и другие виды мяса, контролировать прожарку.\\n3. Смешивать мясо с banchan, заворачивать в салат (ssam), запивать soju.\\n4. Завершить вечер походом в noraebang (караоке) для усиления социальных связей.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны типичные banchan, дым, запахи, формат обслуживания, значение soju, и то, как BBQ служит укреплению дружбы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 50 едди, получает 600 XP, бафф +25% HP на 12 часов, +10 силы и бонус к дружбе (+5) благодаря общему застолью.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Корейское барбекю» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

