INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-los-angeles-santa-monica-pier-2020-2029', 'Лос-Анджелес: пирс Санта-Моники', 'Игрок исследует пирс, катается на колесе обозрения, играет в аркады, рыбачит и завершает миссию закатом.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_pier", "text": "Исследовать Santa Monica Pier", "type": "interact", "target": "santa_monica_pier", "count": 1, "optional": false}, {"id": "ride_ferris_wheel", "text": "Прокатиться на колесе обозрения Pacific Park", "type": "interact", "target": "pacific_park_ferris_wheel", "count": 1, "optional": false}, {"id": "play_arcade", "text": "Поиграть в аркады на пирсе", "type": "interact", "target": "pier_arcade", "count": 1, "optional": false}, {"id": "fish_off_pier", "text": "Порыбачить с пирса", "type": "interact", "target": "pier_fishing", "count": 1, "optional": true}, {"id": "watch_sunset", "text": "Наблюдать закат и посетить точку «End of Route 66»", "type": "interact", "target": "route_66_end_point", "count": 1, "optional": false}]'::jsonb, '{"xp": 1200, "currency": 500, "items": [], "reputation": {"beach_culture": 40}, "achievements": [{"id": "santa_monica_visitor", "name": "Посетитель Santa Monica Pier"}], "skill_boosts": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-los-angeles-santa-monica-pier-2020-2029", "title": "Лос-Анджелес: пирс Санта-Моники", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["los-angeles", "beach", "quest"], "topics": ["leisure", "tourism", "quests"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "social-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/los-angeles/2020-2029/quest-004-santa-monica-pier.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Пляжная линия Лос-Анджелеса нуждалась в лёгком квесте, демонстрирующем исторический Santa Monica Pier и его развлечения.", "goal": "Показать игроку семейный досуг, аркады и закат на Тихом океане с туристической перспективой.", "essence": "Игрок исследует пирс, катается на колесе обозрения, играет в аркады, рыбачит и завершает миссию закатом.", "key_points": ["Пирс объединяет аттракционы Pacific Park, аркады и точку «End of Route 66».", "Квест расслабляющий, уменьшает стресс и развивает пляжный нарратив.", "Награды дают опыт, лёгкие траты и атмосферное достижение."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_pier", "text": "Исследовать Santa Monica Pier", "type": "interact", "target": "santa_monica_pier", "count": 1, "optional": false}, {"id": "ride_ferris_wheel", "text": "Прокатиться на колесе обозрения Pacific Park", "type": "interact", "target": "pacific_park_ferris_wheel", "count": 1, "optional": false}, {"id": "play_arcade", "text": "Поиграть в аркады на пирсе", "type": "interact", "target": "pier_arcade", "count": 1, "optional": false}, {"id": "fish_off_pier", "text": "Порыбачить с пирса", "type": "interact", "target": "pier_fishing", "count": 1, "optional": true}, {"id": "watch_sunset", "text": "Наблюдать закат и посетить точку «End of Route 66»", "type": "interact", "target": "route_66_end_point", "count": 1, "optional": false}], "rewards": {"xp": 1200, "currency": 500, "items": [], "reputation": {"beach_culture": 40}, "achievements": [{"id": "santa_monica_visitor", "name": "Посетитель Santa Monica Pier"}], "skill_boosts": {}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок проводит день на пирсе Санта-Моники, участвуя в туристических активностях и знакомясь с культурой западного\\nпобережья. Атмосфера подчёркивает неоновые огни колеса и шум океана.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Прогулка по пирсу и знакомство с историческими зонами.\\n2. Катание на Ferris Wheel и наблюдение за панорамой LA.\\n3. Посещение аркадного зала Pacific Park и участие в мини-играх.\\n4. Рыбалка с пирса и взаимодействие с местными NPC.\\n5. Финал на закате и коллективное фото у знака «End of the Trail».\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Пирс открыт в 1909 году и остаётся символом курортной Калифорнии.\\n- Колесо обозрения с LED-подсветкой и аркады — визитная карточка Pacific Park.\\n- Пирс является официальным завершением легендарной трассы Route 66.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 600 XP.\\n- Финансовые расходы: -50.\\n- Снижение стресса на 40%.\\n- Достижение «Закат Санта-Моники».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристические активности и фото-сессии.\\n- Мини-игры (аркады, рыбалка).\\n- Атмосферные эффекты и расслабляющие баффы.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест ведёт к сюжетам о побережье Лос-Анджелеса, серфинге, фестивалях и романтических линиях.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1126, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «Пирс Санта-Моники» с туристическими и релаксовыми механиками."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

