INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-taj-mahal-trip', 'Дели 2020-2029 — Поездка к Тадж-Махалу', 'Игрок выезжает из Дели, прибывает в Агру и наблюдает Тадж-Махал на рассвете, фиксируя изменения цвета мрамора.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-taj-mahal-trip", "title": "Дели 2020-2029 — Поездка к Тадж-Махалу", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:10:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["delhi", "taj_mahal", "heritage"], "topics": ["world-wonders", "romantic-legend"], "related_systems": ["narrative-service", "tourism-system", "buff-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/delhi/2020-2029/quest-001-taj-mahal-trip.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий поездки к Тадж-Махалу оставался в Markdown и не учитывал туристические механики.", "goal": "Описать путешествие из Дели в Агру в формате YAML с акцентом на архитектуру и романтическое наследие.", "essence": "Игрок выезжает из Дели, прибывает в Агру и наблюдает Тадж-Махал на рассвете, фиксируя изменения цвета мрамора.", "key_points": ["Квест-id DELHI-2029-001, сложность medium, длительность 8+ часов с дорогой и экскурсиями.", "Этапы охватывают транспорт, прибытие, тур по мавзолею и рассвет/закат.", "Награды дают 5 000 XP, +30 к культурному ресурсу и титул «Свидетель вечной любви»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Поездка из Дели в Агру по классическому маршруту раскрывает историю Тадж-Махала, построенного Шах-Джаханом в память о Мумтаз-Махал,\\nи демонстрирует уникальные свойства белого мрамора.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Организовать выезд из Дели (поезд или автомобиль, около 4 часов).\\n2. Прибыть в Агру и подготовиться к посещению комплекса.\\n3. Исследовать Тадж-Махал: симметрия, инкрустации, история создания.\\n4. Наблюдать рассвет или закат, фиксируя смену оттенков мрамора.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Отмечены 28 типов драгоценных камней в отделке, участие 20 тысяч мастеров и статус Тадж-Махала как одного из новых чудес света.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 50 едди на поездку, получает 5 000 XP, +30 к культурному ресурсу и титул «Свидетель вечной любви».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 256, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Поездка к Тадж-Махалу» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

